import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  PieChart,
  Pie,
  Cell,
  Tooltip,
  Legend,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  ResponsiveContainer,
} from 'recharts';

interface Transaction {
  _id: string;
  date: string;
  amount: number;
  category: string;
  description?: string;
  user_id: string;
  status: 'Paid' | 'Pending';
}

function Dashboard() {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [filtered, setFiltered] = useState<Transaction[]>([]);
  const [search, setSearch] = useState('');
  const [month, setMonth] = useState('');
  const [status, setStatus] = useState('');
  const [category, setCategory] = useState('');
  const [minAmount, setMinAmount] = useState('');
  const [maxAmount, setMaxAmount] = useState('');
  const [error, setError] = useState('');

  const navigate = useNavigate();

  const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff7f50', '#00c49f', '#ffb6b9'];

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });

        const data = Array.isArray(res.data) ? res.data : res.data.transactions || [];
        setTransactions(data);
        setFiltered(data);
      } catch (err) {
        setError('Failed to load transactions');
      }
    };

    fetchTransactions();
  }, []);

  useEffect(() => {
    let result = [...transactions];

    if (month) {
      result = result.filter((t) => new Date(t.date).getMonth() + 1 === Number(month));
    }

    if (status) {
      result = result.filter((t) => t.status === status);
    }

    if (category) {
      result = result.filter((t) => t.category === category);
    }

    if (minAmount) {
      result = result.filter((t) => t.amount >= parseFloat(minAmount));
    }

    if (maxAmount) {
      result = result.filter((t) => t.amount <= parseFloat(maxAmount));
    }

    if (search) {
      result = result.filter((t) =>
        t.category?.toLowerCase().includes(search.toLowerCase())
      );
    }

    setFiltered(result);
  }, [search, month, status, category, minAmount, maxAmount, transactions]);

  const exportToCSV = () => {
    const header = 'User ID,Status,Date,Amount,Category,Description\n';
    const rows = filtered
      .map((t) => {
        const date = new Date(t.date).toISOString().split('T')[0];
        return `${t.user_id},${t.status},${date},${t.amount},${t.category || 'N/A'},${t.description || '-'}`;
      })
      .join('\n');

    const blob = new Blob([header + rows], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);

    const a = document.createElement('a');
    a.href = url;
    a.download = 'transactions.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const categoryOptions = Array.from(new Set(transactions.map((t) => t.category)));

  const categoryData = Object.entries(
    filtered.reduce((acc, t) => {
      const key = t.category || 'Uncategorized';
      acc[key] = (acc[key] || 0) + t.amount;
      return acc;
    }, {} as Record<string, number>)
  ).map(([name, value]) => ({ name, value }));

  const monthlyData = Array.from({ length: 12 }, (_, i) => {
    const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
    const total = filtered
      .filter((t) => new Date(t.date).getMonth() === i)
      .reduce((sum, t) => sum + t.amount, 0);

    return { name: monthName, total };
  });

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-blue-600">ðŸ“Š Dashboard</h1>
        <button
          onClick={handleLogout}
          className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Logout
        </button>
      </div>

      {error && <p className="text-red-500 mb-4">{error}</p>}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
        <select
          className="p-2 border rounded"
          value={month}
          onChange={(e) => setMonth(e.target.value)}
        >
          <option value="">All Months</option>
          {Array.from({ length: 12 }, (_, i) => (
            <option key={i + 1} value={i + 1}>
              {new Date(0, i).toLocaleString('default', { month: 'long' })}
            </option>
          ))}
        </select>

        <select
          className="p-2 border rounded"
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        >
          <option value="">All Status</option>
          <option value="Paid">Paid</option>
          <option value="Pending">Pending</option>
        </select>

        <select
          className="p-2 border rounded"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="">All Categories</option>
          {categoryOptions.map((cat) => (
            <option key={cat} value={cat}>
              {cat}
            </option>
          ))}
        </select>

        <input
          type="number"
          placeholder="Min Amount"
          className="p-2 border rounded"
          value={minAmount}
          onChange={(e) => setMinAmount(e.target.value)}
        />

        <input
          type="number"
          placeholder="Max Amount"
          className="p-2 border rounded"
          value={maxAmount}
          onChange={(e) => setMaxAmount(e.target.value)}
        />

        <input
          type="text"
          placeholder="Search category..."
          className="p-2 border rounded"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      <div className="mb-4">
        <button
          onClick={exportToCSV}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Export CSV
        </button>
      </div>

      <div className="overflow-x-auto mb-10">
        <table className="w-full bg-white rounded shadow-md">
          <thead className="bg-blue-600 text-white">
            <tr>
              <th className="p-3 text-left">User ID</th>
              <th className="p-3 text-left">Date</th>
              <th className="p-3 text-left">Amount (â‚¹)</th>
              <th className="p-3 text-left">Category</th>
              <th className="p-3 text-left">Description</th>
              <th className="p-3 text-left">Status</th>
            </tr>
          </thead>
          <tbody>
            {filtered.length === 0 ? (
              <tr>
                <td colSpan={6} className="p-4 text-center text-gray-500">
                  No transactions found.
                </td>
              </tr>
            ) : (
              filtered.map((t) => (
                <tr key={t._id} className="border-t hover:bg-gray-50">
                  <td className="p-3">{t.user_id}</td>
                  <td className="p-3">{new Date(t.date).toLocaleDateString()}</td>
                  <td className="p-3">â‚¹{t.amount}</td>
                  <td className="p-3">{t.category || 'N/A'}</td>
                  <td className="p-3">{t.description || '-'}</td>
                  <td
                    className={`p-3 font-medium ${
                      t.status === 'Paid' ? 'text-green-600' : 'text-yellow-600'
                    }`}
                  >
                    {t.status}
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      <h2 className="text-xl font-semibold mt-8 mb-4 text-blue-600">ðŸ“ˆ Spending by Category</h2>
      <ResponsiveContainer width="100%" height={300}>
        <PieChart>
          <Pie
            data={categoryData}
            dataKey="value"
            nameKey="name"
            cx="50%"
            cy="50%"
            outerRadius={100}
            fill="#8884d8"
            label
          >
            {categoryData.map((_, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </ResponsiveContainer>

      <h2 className="text-xl font-semibold mt-8 mb-4 text-green-600">ðŸ“Š Monthly Spending</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={monthlyData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="total" fill="#82ca9d" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default Dashboard;
