import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer
} from 'recharts';
import { FiHome, FiCreditCard, FiWallet, FiBarChart2, FiUser, FiMessageCircle, FiSettings, FiLogOut, FiSearch, FiBell, FiSun, FiMoon } from 'react-icons/fi';

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

const sidebarLinks = [
  { name: 'Dashboard', icon: <FiHome />, active: true },
  { name: 'Transactions', icon: <FiCreditCard /> },
  { name: 'Wallet', icon: <FiWallet /> },
  { name: 'Analytics', icon: <FiBarChart2 /> },
  { name: 'Personal', icon: <FiUser /> },
  { name: 'Message', icon: <FiMessageCircle /> },
  { name: 'Setting', icon: <FiSettings /> },
];

const avatarUrl = "https://randomuser.me/api/portraits/men/75.jpg";

function Dashboard() {
  // --- STATE AND LOGIC (from your code) ---
  const [transactions, setTransactions] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState('');
  const [darkMode, setDarkMode] = useState(() => {
    if (typeof window !== "undefined") {
      const stored = localStorage.getItem('dashboard-darkmode');
      if (stored) return stored === 'true';
      return window.matchMedia('(prefers-color-scheme: dark)').matches;
    }
    return false;
  });
  const navigate = useNavigate();

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('dashboard-darkmode', 'true');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('dashboard-darkmode', 'false');
    }
  }, [darkMode]);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = Array.isArray(res.data) ? res.data : res.data.transactions || [];
        setTransactions(data);
        setFiltered(data);
      } catch (err) {}
    };
    fetchTransactions();
  }, []);

  useEffect(() => {
    let result = [...transactions];
    if (search.trim()) {
      const s = search.trim().toLowerCase();
      result = result.filter((t) =>
        t.user_id?.toLowerCase().includes(s) ||
        t.status?.toLowerCase().includes(s) ||
        t.category?.toLowerCase().includes(s) ||
        t.description?.toLowerCase().includes(s) ||
        t.amount.toString().includes(s) ||
        new Date(t.date).toLocaleDateString().toLowerCase().includes(s)
      );
    }
    setFiltered(result);
  }, [search, transactions]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  // --- SUMMARY METRICS LOGIC ---
  const balance = 41210;
  const revenue = 41210;
  const expenses = 41210;
  const savings = 41210;

  // --- OVERVIEW CHART DATA ---
  const chartData = [
    { name: 'Jan', income: 400, expenses: 240 },
    { name: 'Feb', income: 700, expenses: 400 },
    { name: 'Mar', income: 300, expenses: 240 },
    { name: 'Apr', income: 900, expenses: 800 },
    { name: 'May', income: 600, expenses: 300 },
    { name: 'Jun', income: 1100, expenses: 600 },
    { name: 'Jul', income: 224, expenses: 700 },
    { name: 'Aug', income: 900, expenses: 900 },
    { name: 'Sep', income: 700, expenses: 800 },
    { name: 'Oct', income: 800, expenses: 1000 },
    { name: 'Nov', income: 600, expenses: 700 },
    { name: 'Dec', income: 900, expenses: 600 },
  ];

  // --- RECENT TRANSACTIONS MOCK ---
  const recentTransactions = [
    {
      name: "Matheus Ferrero",
      avatar: "https://randomuser.me/api/portraits/men/32.jpg",
      status: "Completed",
      amount: "+$54.08",
      color: "text-green-400",
      desc: "Transfer from Matheus Ferrero"
    },
    {
      name: "Floyd Miles",
      avatar: "https://randomuser.me/api/portraits/men/45.jpg",
      status: "Completed",
      amount: "-$39.65",
      color: "text-yellow-400",
      desc: "Transfer to Floyd Miles"
    },
    {
      name: "Jerome Bell",
      avatar: "https://randomuser.me/api/portraits/men/76.jpg",
      status: "Pending",
      amount: "-$29.78",
      color: "text-yellow-400",
      desc: "Transfer to Jerome Bell"
    },
  ];

  // --- TABLE DATA ---
  const tableRows = [
    {
      name: "Matheus Ferrero",
      avatar: "https://randomuser.me/api/portraits/men/32.jpg",
      date: "Sat, 20 Apr 2020",
      amount: "+$80.09",
      status: "Completed",
      statusColor: "bg-green-600",
    },
    {
      name: "Floyd Miles",
      avatar: "https://randomuser.me/api/portraits/men/45.jpg",
      date: "Fri, 19 Apr 2020",
      amount: "-$7.03",
      status: "Completed",
      statusColor: "bg-green-600",
    },
    {
      name: "Jerome Bell",
      avatar: "https://randomuser.me/api/portraits/men/76.jpg",
      date: "Tue, 19 Apr 2020",
      amount: "-$30.09",
      status: "Pending",
      statusColor: "bg-yellow-500",
    },
  ];

  return (
    <div className="flex min-h-screen bg-gray-900 text-gray-100 font-sans">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-800 flex flex-col px-6 py-8">
        <div className="flex items-center mb-10">
          <div className="bg-gradient-to-tr from-green-400 to-blue-500 rounded-full w-10 h-10 flex items-center justify-center text-2xl font-bold mr-3">
            J
          </div>
          <span className="text-2xl font-bold tracking-wide">Penta</span>
        </div>
        <nav className="flex-1">
          <ul>
            {sidebarLinks.map(link => (
              <li key={link.name} className="mb-2">
                <a
                  href="#"
                  className={classNames(
                    "flex items-center gap-3 px-4 py-3 rounded-lg transition",
                    link.active
                      ? "bg-gray-900 text-green-400 font-bold"
                      : "hover:bg-gray-700 text-gray-300"
                  )}
                >
                  <span className="text-xl">{link.icon}</span>
                  {link.name}
                </a>
              </li>
            ))}
          </ul>
        </nav>
        <button
          onClick={handleLogout}
          className="flex items-center gap-2 mt-12 text-gray-400 hover:text-red-500 transition"
        >
          <FiLogOut className="text-xl" />
          Logout
        </button>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Top Bar */}
        <header className="flex items-center justify-between px-10 py-6 bg-gray-900 border-b border-gray-800">
          <h2 className="text-2xl font-bold">Dashboard</h2>
          <div className="flex items-center gap-4">
            <div className="relative">
              <FiSearch className="absolute left-3 top-2.5 text-gray-400" />
              <input
                type="text"
                className="bg-gray-800 rounded-full pl-10 pr-4 py-2 text-gray-100 focus:outline-none w-64"
                placeholder="Search..."
                value={search}
                onChange={e => setSearch(e.target.value)}
              />
            </div>
            <button
              onClick={() => setDarkMode(d => !d)}
              className="rounded-full p-2 bg-gray-800 hover:bg-gray-700 transition"
              title="Toggle dark mode"
            >
              {darkMode ? <FiSun className="text-yellow-300" /> : <FiMoon />}
            </button>
            <button className="relative p-2 bg-gray-800 rounded-full hover:bg-gray-700">
              <FiBell />
              <span className="absolute top-1 right-1 w-2 h-2 bg-green-400 rounded-full"></span>
            </button>
            <img src={avatarUrl} alt="avatar" className="w-9 h-9 rounded-full object-cover border-2 border-gray-700" />
          </div>
        </header>

        {/* Dashboard Content */}
        <main className="flex-1 p-10 bg-gray-900">
          {/* Summary Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <div className="bg-gray-800 rounded-xl p-6 flex flex-col items-center">
              <span className="mb-2 text-2xl bg-gray-900 p-3 rounded-full text-green-400">
                <FiWallet />
              </span>
              <span className="uppercase text-gray-400 text-xs mb-1">Balance</span>
              <span className="text-2xl font-bold">${balance.toLocaleString()}</span>
            </div>
            <div className="bg-gray-800 rounded-xl p-6 flex flex-col items-center">
              <span className="mb-2 text-2xl bg-gray-900 p-3 rounded-full text-green-400">
                <FiBarChart2 />
              </span>
              <span className="uppercase text-gray-400 text-xs mb-1">Revenue</span>
              <span className="text-2xl font-bold">${revenue.toLocaleString()}</span>
            </div>
            <div className="bg-gray-800 rounded-xl p-6 flex flex-col items-center">
              <span className="mb-2 text-2xl bg-gray-900 p-3 rounded-full text-green-400">
                <FiCreditCard />
              </span>
              <span className="uppercase text-gray-400 text-xs mb-1">Expenses</span>
              <span className="text-2xl font-bold">${expenses.toLocaleString()}</span>
            </div>
            <div className="bg-gray-800 rounded-xl p-6 flex flex-col items-center">
              <span className="mb-2 text-2xl bg-gray-900 p-3 rounded-full text-green-400">
                <FiUser />
              </span>
              <span className="uppercase text-gray-400 text-xs mb-1">Savings</span>
              <span className="text-2xl font-bold">${savings.toLocaleString()}</span>
            </div>
          </div>

          {/* Overview & Recent Transactions */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            {/* Overview Chart */}
            <div className="bg-gray-800 rounded-xl p-6 md:col-span-2">
              <div className="flex justify-between items-center mb-4">
                <span className="font-bold text-lg">Overview</span>
                <div className="flex items-center gap-4 text-sm">
                  <span className="flex items-center gap-1 text-green-400"><span className="w-2 h-2 bg-green-400 rounded-full"></span> Income</span>
                  <span className="flex items-center gap-1 text-yellow-400"><span className="w-2 h-2 bg-yellow-400 rounded-full"></span> Expenses</span>
                  <select className="bg-gray-900 text-gray-200 rounded px-2 py-1 text-xs border border-gray-700">
                    <option>Monthly</option>
                  </select>
                </div>
              </div>
              <ResponsiveContainer width="100%" height={220}>
                <LineChart data={chartData}>
                  <XAxis dataKey="name" stroke="#6b7280" />
                  <YAxis stroke="#6b7280" />
                  <Tooltip
                    contentStyle={{ background: "#222", border: "none", borderRadius: 8, color: "#fff" }}
                  />
                  <Line type="monotone" dataKey="income" stroke="#22d3ee" strokeWidth={3} dot={false} />
                  <Line type="monotone" dataKey="expenses" stroke="#fbbf24" strokeWidth={3} dot={false} />
                </LineChart>
              </ResponsiveContainer>
            </div>
            {/* Recent Transactions */}
            <div className="bg-gray-800 rounded-xl p-6 flex flex-col">
              <div className="flex justify-between items-center mb-4">
                <span className="font-bold text-lg">Recent Transaction</span>
                <a href="#" className="text-green-400 text-xs">See all</a>
              </div>
              <div className="flex flex-col gap-4">
                {recentTransactions.map((tx, idx) => (
                  <div key={idx} className="flex items-center gap-3">
                    <img src={tx.avatar} className="w-10 h-10 rounded-full object-cover" alt={tx.name} />
                    <div className="flex-1">
                      <div className="text-sm text-gray-100">{tx.desc}</div>
                      <div className="text-xs text-gray-400">{tx.name}</div>
                    </div>
                    <div className={`font-bold ${tx.color}`}>{tx.amount}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Transactions Table */}
          <div className="bg-gray-800 rounded-xl p-6">
            <div className="flex justify-between items-center mb-4">
              <span className="font-bold text-lg">Transactions</span>
              <div className="flex gap-2">
                <div className="relative">
                  <FiSearch className="absolute left-3 top-2.5 text-gray-400" />
                  <input
                    type="text"
                    className="bg-gray-900 rounded-full pl-10 pr-4 py-2 text-gray-100 focus:outline-none w-64"
                    placeholder="Search for anything..."
                    value={search}
                    onChange={e => setSearch(e.target.value)}
                  />
                </div>
                <input
                  type="text"
                  className="bg-gray-900 rounded-full px-4 py-2 text-gray-100 focus:outline-none text-sm"
                  placeholder="10 May - 20 May"
                  disabled
                />
              </div>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full text-left">
                <thead>
                  <tr className="text-gray-400 text-xs uppercase">
                    <th className="py-3 px-2">Name</th>
                    <th className="py-3 px-2">Date</th>
                    <th className="py-3 px-2">Amount</th>
                    <th className="py-3 px-2">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {tableRows.map((row, idx) => (
                    <tr key={idx} className="border-b border-gray-700 last:border-0 hover:bg-gray-700/30">
                      <td className="py-3 px-2 flex items-center gap-3">
                        <img src={row.avatar} className="w-8 h-8 rounded-full object-cover" alt={row.name} />
                        <span>{row.name}</span>
                      </td>
                      <td className="py-3 px-2">{row.date}</td>
                      <td className={classNames("py-3 px-2 font-bold", row.amount.startsWith('+') ? "text-green-400" : "text-yellow-400")}>{row.amount}</td>
                      <td className="py-3 px-2">
                        <span className={classNames("text-xs px-3 py-1 rounded-full font-bold", row.statusColor, "text-gray-900")}>
                          {row.status}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

export default Dashboard;
