import { useEffect, useState, useMemo } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  PieChart, Pie, Cell, Tooltip, Legend, BarChart, Bar,
  XAxis, YAxis, CartesianGrid, ResponsiveContainer
} from 'recharts';

interface Transaction {
  _id: string;
  date: string;
  amount: number;
  category: string;
  description?: string;
  user_id: string;
  status: 'Paid' | 'Pending';
}

type SortKey = keyof Transaction | 'amount' | 'date' | 'status';

function Dashboard() {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [filtered, setFiltered] = useState<Transaction[]>([]);
  const [month, setMonth] = useState('');
  const [status, setStatus] = useState('');
  const [category, setCategory] = useState('');
  const [user, setUser] = useState('');
  const [minAmount, setMinAmount] = useState('');
  const [maxAmount, setMaxAmount] = useState('');
  const [groupByWeek, setGroupByWeek] = useState(false);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 10;
  const [error, setError] = useState('');
  const [sortConfig, setSortConfig] = useState<{ key: SortKey; direction: 'asc' | 'desc' } | null>(null);

  const navigate = useNavigate();
  const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff7f50', '#00c49f', '#ffb6b9'];

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = Array.isArray(res.data) ? res.data : res.data.transactions || [];
        setTransactions(data);
        setFiltered(data);
      } catch (err) {
        setError('Failed to load transactions');
      }
    };
    fetchTransactions();
  }, []);

  useEffect(() => {
    let result = [...transactions];
    if (month) result = result.filter((t) => new Date(t.date).getMonth() + 1 === Number(month));
    if (status) result = result.filter((t) => t.status === status);
    if (category) result = result.filter((t) => t.category === category);
    if (user) result = result.filter((t) => t.user_id === user);
    if (minAmount) result = result.filter((t) => t.amount >= parseFloat(minAmount));
    if (maxAmount) result = result.filter((t) => t.amount <= parseFloat(maxAmount));
    if (startDate) result = result.filter((t) => new Date(t.date) >= new Date(startDate));
    if (endDate) result = result.filter((t) => new Date(t.date) <= new Date(endDate));
    setCurrentPage(1);
    setFiltered(result);
  }, [month, status, category, user, minAmount, maxAmount, startDate, endDate, transactions]);

  // Sorting logic
  const onSort = (key: SortKey) => {
    let direction: 'asc' | 'desc' = 'asc';
    if (sortConfig && sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  const sortedFiltered = useMemo(() => {
    if (!sortConfig) return filtered;
    const sorted = [...filtered].sort((a, b) => {
      let aValue = a[sortConfig.key as keyof Transaction];
      let bValue = b[sortConfig.key as keyof Transaction];
      // Special handling for "amount", "date", "status"
      if (sortConfig.key === 'date') {
        aValue = new Date(aValue as string).getTime();
        bValue = new Date(bValue as string).getTime();
      }
      if (sortConfig.key === 'amount') {
        aValue = Number(aValue);
        bValue = Number(bValue);
      }
      if (sortConfig.key === 'status') {
        aValue = aValue === 'Paid' ? 1 : 0;
        bValue = bValue === 'Paid' ? 1 : 0;
      }
      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return sortConfig.direction === 'asc'
          ? aValue.localeCompare(bValue)
          : bValue.localeCompare(aValue);
      }
      if (typeof aValue === 'number' && typeof bValue === 'number') {
        return sortConfig.direction === 'asc' ? aValue - bValue : bValue - aValue;
      }
      return 0;
    });
    return sorted;
  }, [filtered, sortConfig]);

  const exportToCSV = () => {
    const header = 'User ID,Status,Date,Amount,Category,Description\n';
    const rows = sortedFiltered
      .map((t) => {
        const date = new Date(t.date).toISOString().split('T')[0];
        return `${t.user_id},${t.status},${date},${t.amount},${t.category || 'N/A'},${t.description || '-'}`;
      })
      .join('\n');
    const blob = new Blob([header + rows], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'transactions.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const uniqueCategories = Array.from(new Set(transactions.map((t) => t.category)));
  const uniqueUsers = Array.from(new Set(transactions.map((t) => t.user_id)));

  const getWeek = (dateStr: string) => {
    const date = new Date(dateStr);
    const firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
    const dayOfWeek = firstDay.getDay();
    const adjustedDate = date.getDate() + dayOfWeek;
    return Math.ceil(adjustedDate / 7);
  };

  const categoryData = Object.entries(
    sortedFiltered.reduce((acc, t) => {
      const key = t.category || 'Uncategorized';
      acc[key] = (acc[key] || 0) + t.amount;
      return acc;
    }, {} as Record<string, number>)
  ).map(([name, value]) => ({ name, value }));

  const barData = groupByWeek
    ? Array.from({ length: 12 }, (_, i) => {
        const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
        const weeks = [0, 0, 0, 0, 0];
        sortedFiltered
          .filter((t) => new Date(t.date).getMonth() === i)
          .forEach((t) => {
            const week = getWeek(t.date);
            weeks[week - 1] += t.amount;
          });
        return { name: monthName, ...weeks.reduce((acc, val, idx) => ({ ...acc, [`W${idx + 1}`]: val }), {}) };
      })
    : Array.from({ length: 12 }, (_, i) => {
        const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
        const total = sortedFiltered
          .filter((t) => new Date(t.date).getMonth() === i)
          .reduce((sum, t) => sum + t.amount, 0);
        return { name: monthName, total };
      });

  const totalEntries = sortedFiltered.length;
  const totalPages = Math.ceil(totalEntries / rowsPerPage);
  const startIndex = (currentPage - 1) * rowsPerPage;
  const currentEntries = sortedFiltered.slice(startIndex, startIndex + rowsPerPage);

  // --- SUMMARY METRICS LOGIC ---
  const totalAmount = sortedFiltered.reduce((sum, t) => sum + t.amount, 0);
  const totalPaid = sortedFiltered.filter(t => t.status === 'Paid').reduce((sum, t) => sum + t.amount, 0);
  const totalPending = sortedFiltered.filter(t => t.status === 'Pending').reduce((sum, t) => sum + t.amount, 0);

  // Helper for indicator
  const sortIndicator = (key: SortKey) =>
    sortConfig?.key === key ? (sortConfig.direction === 'asc' ? '▲' : '▼') : '';

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-blue-600">📊 Dashboard</h1>
        <button
          onClick={handleLogout}
          className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Logout
        </button>
      </div>

      {/* --- SUMMARY METRICS SECTION (Sortable) --- */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <div
          onClick={() => onSort('_id')}
          className="bg-white rounded shadow p-4 flex flex-col items-center cursor-pointer hover:bg-gray-50"
        >
          <span className="text-gray-500 text-sm">Total Transactions</span>
          <span className="text-2xl font-bold text-blue-700">
            {sortedFiltered.length} {sortIndicator('_id')}
          </span>
        </div>
        <div
          onClick={() => onSort('amount')}
          className="bg-white rounded shadow p-4 flex flex-col items-center cursor-pointer hover:bg-gray-50"
        >
          <span className="text-gray-500 text-sm">Total Amount</span>
          <span className="text-2xl font-bold text-green-700">
            ₹{totalAmount.toLocaleString()} {sortIndicator('amount')}
          </span>
        </div>
        <div
          onClick={() => onSort('status')}
          className="bg-white rounded shadow p-4 flex flex-col items-center cursor-pointer hover:bg-gray-50"
        >
          <span className="text-gray-500 text-sm">Paid Amount</span>
          <span className="text-2xl font-bold text-green-600">
            ₹{totalPaid.toLocaleString()} {sortIndicator('status')}
          </span>
        </div>
        <div
          onClick={() => onSort('status')}
          className="bg-white rounded shadow p-4 flex flex-col items-center cursor-pointer hover:bg-gray-50"
        >
          <span className="text-gray-500 text-sm">Pending Amount</span>
          <span className="text-2xl font-bold text-yellow-600">
            ₹{totalPending.toLocaleString()} {sortIndicator('status')}
          </span>
        </div>
      </div>
      {/* --- END SUMMARY METRICS SECTION --- */}

      {error && <p className="text-red-500 mb-4">{error}</p>}

      {/* Filters */}
      {/* ... (unchanged, keep your filter code here) ... */}

      {/* Table */}
      <div className="overflow-x-auto mb-2">
        <table className="w-full bg-white rounded shadow-md">
          <thead className="bg-blue-600 text-white sticky top-0 z-10">
            <tr>
              <th
                onClick={() => onSort('user_id')}
                className="p-3 text-left cursor-pointer"
              >
                User ID {sortIndicator('user_id')}
              </th>
              <th
                onClick={() => onSort('date')}
                className="p-3 text-left cursor-pointer"
              >
                Date {sortIndicator('date')}
              </th>
              <th
                onClick={() => onSort('amount')}
                className="p-3 text-left cursor-pointer"
              >
                Amount (₹) {sortIndicator('amount')}
              </th>
              <th
                onClick={() => onSort('category')}
                className="p-3 text-left cursor-pointer"
              >
                Category {sortIndicator('category')}
              </th>
              <th className="p-3 text-left">Description</th>
              <th
                onClick={() => onSort('status')}
                className="p-3 text-left cursor-pointer"
              >
                Status {sortIndicator('status')}
              </th>
            </tr>
          </thead>
          <tbody>
            {currentEntries.length === 0 ? (
              <tr>
                <td colSpan={6} className="p-4 text-center text-gray-500">No transactions found.</td>
              </tr>
            ) : (
              currentEntries.map((t) => (
                <tr key={t._id} className="border-t hover:bg-gray-50">
                  <td className="p-3">{t.user_id}</td>
                  <td className="p-3">{new Date(t.date).toLocaleDateString()}</td>
                  <td className="p-3">₹{t.amount}</td>
                  <td className="p-3">{t.category || 'N/A'}</td>
                  <td className="p-3">{t.description || '-'}</td>
                  <td className={`p-3 font-medium ${t.status === 'Paid' ? 'text-green-600' : 'text-yellow-600'}`}>{t.status}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* ... (rest of your dashboard code unchanged) ... */}
    </div>
  );
}

export default Dashboard;
