import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  PieChart,
  Pie,
  Cell,
  Tooltip,
  Legend,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  ResponsiveContainer,
} from 'recharts';

interface Transaction {
  _id: string;
  date: string;
  amount: number;
  category: string;
  description?: string;
  user_id: string;
  status: 'Paid' | 'Pending';
}

function Dashboard() {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [filtered, setFiltered] = useState<Transaction[]>([]);
  const [month, setMonth] = useState('');
  const [status, setStatus] = useState('');
  const [category, setCategory] = useState('');
  const [user, setUser] = useState('');
  const [minAmount, setMinAmount] = useState('');
  const [maxAmount, setMaxAmount] = useState('');
  const [groupByWeek, setGroupByWeek] = useState(false);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [error, setError] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  const navigate = useNavigate();
  const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff7f50', '#00c49f', '#ffb6b9'];

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = Array.isArray(res.data) ? res.data : res.data.transactions || [];
        setTransactions(data);
        setFiltered(data);
      } catch (err) {
        setError('Failed to load transactions');
      }
    };

    fetchTransactions();
  }, []);

  useEffect(() => {
    let result = [...transactions];

    if (month) result = result.filter((t) => new Date(t.date).getMonth() + 1 === Number(month));
    if (status) result = result.filter((t) => t.status === status);
    if (category) result = result.filter((t) => t.category === category);
    if (user) result = result.filter((t) => t.user_id === user);
    if (minAmount) result = result.filter((t) => t.amount >= parseFloat(minAmount));
    if (maxAmount) result = result.filter((t) => t.amount <= parseFloat(maxAmount));
    if (startDate) result = result.filter((t) => new Date(t.date) >= new Date(startDate));
    if (endDate) result = result.filter((t) => new Date(t.date) <= new Date(endDate));

    setFiltered(result);
    setCurrentPage(1); // reset to first page on filter change
  }, [month, status, category, user, minAmount, maxAmount, startDate, endDate, transactions]);

  const paginatedTransactions = filtered.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const totalPages = Math.ceil(filtered.length / itemsPerPage);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const exportToCSV = () => {
    const header = 'User ID,Status,Date,Amount,Category,Description\n';
    const rows = filtered
      .map((t) => {
        const date = new Date(t.date).toISOString().split('T')[0];
        return `${t.user_id},${t.status},${date},${t.amount},${t.category || 'N/A'},${t.description || '-'}`;
      })
      .join('\n');
    const blob = new Blob([header + rows], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'transactions.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  const uniqueCategories = Array.from(new Set(transactions.map((t) => t.category)));
  const uniqueUsers = Array.from(new Set(transactions.map((t) => t.user_id)));

  const categoryData = Object.entries(
    filtered.reduce((acc, t) => {
      const key = t.category || 'Uncategorized';
      acc[key] = (acc[key] || 0) + t.amount;
      return acc;
    }, {} as Record<string, number>)
  ).map(([name, value]) => ({ name, value }));

  const getWeek = (dateStr: string) => {
    const date = new Date(dateStr);
    const firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
    const dayOfWeek = firstDay.getDay();
    const adjustedDate = date.getDate() + dayOfWeek;
    return Math.ceil(adjustedDate / 7);
  };

  const barData = groupByWeek
    ? Array.from({ length: 12 }, (_, i) => {
        const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
        const weeks = [0, 0, 0, 0, 0];
        filtered
          .filter((t) => new Date(t.date).getMonth() === i)
          .forEach((t) => {
            const week = getWeek(t.date);
            weeks[week - 1] += t.amount;
          });
        return { name: monthName, ...weeks.reduce((acc, val, idx) => ({ ...acc, [`W${idx + 1}`]: val }), {}) };
      })
    : Array.from({ length: 12 }, (_, i) => {
        const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
        const total = filtered
          .filter((t) => new Date(t.date).getMonth() === i)
          .reduce((sum, t) => sum + t.amount, 0);
        return { name: monthName, total };
      });

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-blue-600">📊 Dashboard</h1>
        <button
          onClick={handleLogout}
          className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Logout
        </button>
      </div>

      {error && <p className="text-red-500 mb-4">{error}</p>}

      <div className="grid md:grid-cols-3 lg:grid-cols-6 gap-4 mb-4">
        {/* filters */}
        <select className="p-2 border rounded" value={month} onChange={(e) => setMonth(e.target.value)}>
          <option value="">All Months</option>
          {Array.from({ length: 12 }, (_, i) => (
            <option key={i + 1} value={i + 1}>
              {new Date(0, i).toLocaleString('default', { month: 'long' })}
            </option>
          ))}
        </select>
        <select className="p-2 border rounded" value={status} onChange={(e) => setStatus(e.target.value)}>
          <option value="">All Status</option>
          <option value="Paid">Paid</option>
          <option value="Pending">Pending</option>
        </select>
        <select className="p-2 border rounded" value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">All Categories</option>
          {uniqueCategories.map((cat) => <option key={cat}>{cat}</option>)}
        </select>
        <select className="p-2 border rounded" value={user} onChange={(e) => setUser(e.target.value)}>
          <option value="">All Users</option>
          {uniqueUsers.map((u) => <option key={u}>{u}</option>)}
        </select>
        <input type="number" className="p-2 border rounded" placeholder="Min ₹" value={minAmount} onChange={(e) => setMinAmount(e.target.value)} />
        <input type="number" className="p-2 border rounded" placeholder="Max ₹" value={maxAmount} onChange={(e) => setMaxAmount(e.target.value)} />
      </div>

      <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        <input type="date" className="p-2 border rounded" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
        <input type="date" className="p-2 border rounded" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
        <button onClick={exportToCSV} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">Export CSV</button>
        <button onClick={() => {
          setMonth(''); setStatus(''); setCategory(''); setUser(''); setMinAmount('');
          setMaxAmount(''); setStartDate(''); setEndDate(''); setGroupByWeek(false); setFiltered(transactions);
        }} className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600">Reset Filters</button>
      </div>

      <label className="flex items-center gap-2 mb-4 text-sm">
        <input type="checkbox" checked={groupByWeek} onChange={(e) => setGroupByWeek(e.target.checked)} />
        Group by Week
      </label>

      {/* 📄 Transactions Table */}
      <div className="overflow-x-auto mb-4">
        <table className="w-full bg-white rounded shadow-md">
          <thead className="bg-blue-600 text-white">
            <tr>
              <th className="p-3 text-left">User ID</th>
              <th className="p-3 text-left">Date</th>
              <th className="p-3 text-left">Amount (₹)</th>
              <th className="p-3 text-left">Category</th>
              <th className="p-3 text-left">Description</th>
              <th className="p-3 text-left">Status</th>
            </tr>
          </thead>
          <tbody>
            {paginatedTransactions.length === 0 ? (
              <tr>
                <td colSpan={6} className="p-4 text-center text-gray-500">No transactions found.</td>
              </tr>
            ) : (
              paginatedTransactions.map((t) => (
                <tr key={t._id} className="border-t hover:bg-gray-50">
                  <td className="p-3">{t.user_id}</td>
                  <td className="p-3">{new Date(t.date).toLocaleDateString()}</td>
                  <td className="p-3">₹{t.amount}</td>
                  <td className="p-3">{t.category || 'N/A'}</td>
                  <td className="p-3">{t.description || '-'}</td>
                  <td className={`p-3 font-medium ${t.status === 'Paid' ? 'text-green-600' : 'text-yellow-600'}`}>{t.status}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* ⏮ Pagination Controls */}
      <div className="flex justify-center items-center gap-4 mb-10">
        <button disabled={currentPage === 1} onClick={() => setCurrentPage(p => p - 1)} className="px-4 py-2 rounded bg-gray-300 hover:bg-gray-400 disabled:opacity-50">Previous</button>
        <span className="text-sm font-medium">Page {currentPage} of {totalPages}</span>
        <button disabled={currentPage === totalPages} onClick={() => setCurrentPage(p => p + 1)} className="px-4 py-2 rounded bg-gray-300 hover:bg-gray-400 disabled:opacity-50">Next</button>
      </div>

      {/* 📊 Charts */}
      <h2 className="text-xl font-semibold mt-8 mb-4 text-blue-600">📈 Spending by Category</h2>
      <ResponsiveContainer width="100%" height={300}>
        <PieChart>
          <Pie data={categoryData} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={100} fill="#8884d8" label>
            {categoryData.map((_, i) => (
              <Cell key={i} fill={COLORS[i % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </ResponsiveContainer>

      <h2 className="text-xl font-semibold mt-8 mb-4 text-green-600">📊 Monthly Spending</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={barData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          {groupByWeek
            ? ['W1', 'W2', 'W3', 'W4', 'W5'].map((week, i) => (
                <Bar key={week} dataKey={week} fill={COLORS[i % COLORS.length]} />
              ))
            : <Bar dataKey="total" fill="#82ca9d" />}
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default Dashboard;
