import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

interface Transaction {
  _id: string;
  id: number;
  date: string;
  amount: number;
  category: 'Revenue' | 'Expense';
  status: 'Paid' | 'Pending';
  user_id: string;
  user_profile: string; // image URL or name
}

function Dashboard() {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });

        const data = Array.isArray(res.data) ? res.data : [];
        setTransactions(data);
      } catch (err) {
        setError('Failed to load transactions');
      }
    };

    fetchTransactions();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-blue-600">ðŸ“Š Dashboard</h1>
        <button
          onClick={handleLogout}
          className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Logout
        </button>
      </div>

      {error && <p className="text-red-500 mb-4">{error}</p>}

      <div className="overflow-x-auto">
        <table className="w-full bg-white rounded shadow-md">
          <thead className="bg-blue-600 text-white">
            <tr>
              <th className="p-3 text-left">User</th>
              <th className="p-3 text-left">Date</th>
              <th className="p-3 text-left">Amount (â‚¹)</th>
              <th className="p-3 text-left">Category</th>
              <th className="p-3 text-left">Status</th>
            </tr>
          </thead>
          <tbody>
            {transactions.length === 0 ? (
              <tr>
                <td colSpan={5} className="p-4 text-center text-gray-500">
                  No transactions found.
                </td>
              </tr>
            ) : (
              transactions.map((t) => (
                <tr key={t._id} className="border-t hover:bg-gray-50">
                  <td className="p-3 flex items-center gap-2">
                    {t.user_profile?.startsWith('http') ? (
                      <img
                        src={t.user_profile}
                        alt={t.user_id}
                        className="w-8 h-8 rounded-full border"
                        onError={(e) =>
                          ((e.target as HTMLImageElement).src = 'https://via.placeholder.com/40')
                        }
                      />
                    ) : (
                      <div className="font-semibold">{t.user_profile}</div>
                    )}
                    <div className="text-sm text-gray-700">{t.user_id}</div>
                  </td>
                  <td className="p-3">{new Date(t.date).toLocaleDateString()}</td>
                  <td className="p-3">â‚¹{t.amount}</td>
                  <td className="p-3">{t.category}</td>
                  <td
                    className={`p-3 font-medium ${
                      t.status === 'Paid' ? 'text-green-600' : 'text-yellow-600'
                    }`}
                  >
                    {t.status}
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
