// export default Dashboard;


import React, { useState } from "react";
import {
  PieChart,
  Pie,
  Cell,
  Tooltip,
  Legend,
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
} from "recharts";

// ---- TypeScript Interfaces ----
interface Transaction {
  id: string | number;
  date: string;
  description: string;
  category: string;
  user: string;
  amount: number;
  status: "success" | "pending" | "failed";
}

interface User {
  id: string | number;
  name: string;
}

interface Category {
  id: string | number;
  name: string;
}

interface SummaryMetrics {
  totalTransactions: number;
  totalAmount: number;
  successful: number;
  failed: number;
}

interface CategoryData {
  name: string;
  value: number;
}

interface BarData {
  name: string;
  total?: number;
  W1?: number;
  W2?: number;
  W3?: number;
  W4?: number;
  W5?: number;
}

interface DashboardProps {
  summaryMetrics: SummaryMetrics;
  categoryData: CategoryData[];
  barData: BarData[];
  transactions: Transaction[];
  users: User[];
  categories: Category[];
  handleLogout: () => void;
}

// ---- Chart Colors ----
const COLORS = [
  "#8884d8",
  "#82ca9d",
  "#ffc658",
  "#ff7f50",
  "#00c49f",
  "#ffbb28",
];

// ---- Dashboard Component ----
const Dashboard: React.FC<DashboardProps> = ({
  summaryMetrics,
  categoryData,
  barData,
  transactions,
  users,
  categories,
  handleLogout,
}) => {
  // State
  const [darkMode, setDarkMode] = useState(false);
  const [search, setSearch] = useState("");
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    month: "",
    status: "",
    category: "",
    user: "",
    minAmount: "",
    maxAmount: "",
    startDate: "",
    endDate: "",
  });
  const [groupByWeek, setGroupByWeek] = useState(false);

  // Filtering logic (expand as needed)
  const filteredTransactions = transactions.filter((txn: Transaction) => {
    if (
      search &&
      !txn.description.toLowerCase().includes(search.toLowerCase())
    )
      return false;
    // Add more filter logic here using filters state
    return true;
  });

  // Handle filter input changes
  const handleFilterChange = (
    e: React.ChangeEvent<HTMLSelectElement | HTMLInputElement>
  ) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="flex min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
        {/* Sidebar */}
        <aside className="w-64 bg-white dark:bg-gray-800 p-4 shadow-lg flex-shrink-0">
          <button
            onClick={() => setShowFilters((prev) => !prev)}
            className="w-full bg-blue-600 text-white py-2 rounded mb-4 hover:bg-blue-700 transition"
          >
            Filter
          </button>
          {showFilters && (
            <div className="space-y-4">
              {/* Month */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                  Month
                </label>
                <select
                  name="month"
                  value={filters.month}
                  onChange={handleFilterChange}
                  className="w-full mt-1 p-2 border rounded dark:bg-gray-700 dark:text-gray-100"
                >
                  <option value="">All</option>
                  <option value="1">January</option>
                  <option value="2">February</option>
                  <option value="3">March</option>
                  <option value="4">April</option>
                  <option value="5">May</option>
                  <option value="6">June</option>
                  <option value="7">July</option>
                  <option value="8">August</option>
                  <option value="9">September</option>
                  <option value="10">October</option>
                  <option value="11">November</option>
                  <option value="12">December</option>
                </select>
              </div>
              {/* Status */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                  Status
                </label>
                <select
                  name="status"
                  value={filters.status}
                  onChange={handleFilterChange}
                  className="w-full mt-1 p-2 border rounded dark:bg-gray-700 dark:text-gray-100"
                >
                  <option value="">All</option>
                  <option value="success">Success</option>
                  <option value="pending">Pending</option>
                  <option value="failed">Failed</option>
                </select>
              </div>
              {/* Category */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                  Category
                </label>
                <select
                  name="category"
                  value={filters.category}
                  onChange={handleFilterChange}
                  className="w-full mt-1 p-2 border rounded dark:bg-gray-700 dark:text-gray-100"
                >
                  <option value="">All</option>
                  {categories.map((cat: Category) => (
                    <option key={cat.id} value={cat.name}>
                      {cat.name}
                    </option>
                  ))}
                </select>
              </div>
              {/* User */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                  User
                </label>
                <select
                  name="user"
                  value={filters.user}
                  onChange={handleFilterChange}
                  className="w-full mt-1 p-2 border rounded dark:bg-gray-700 dark:text-gray-100"
                >
                  <option value="">All</option>
                  {users.map((user: User) => (
                    <option key={user.id} value={user.name}>
                      {user.name}
                    </option>
                  ))}
                </select>
              </div>
              {/* Min Amount */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                  Min Amount
                </label>
                <input
                  type="number"
                  name="minAmount"
                  value={filters.minAmount}
                  onChange={handleFilterChange}
                  className="w-full mt-1 p-2 border rounded dark:bg-gray-700 dark:text-gray-100"
                  placeholder="0"
                />
              </div>
              {/* Max Amount */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                  Max Amount
                </label>
                <input
                  type="number"
                  name="maxAmount"
                  value={filters.maxAmount}
                  onChange={handleFilterChange}
                  className="w-full mt-1 p-2 border rounded dark:bg-gray-700 dark:text-gray-100"
                  placeholder="10000"
                />
              </div>
              {/* Start Date */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                  Start Date
                </label>
                <input
                  type="date"
                  name="startDate"
                  value={filters.startDate}
                  onChange={handleFilterChange}
                  className="w-full mt-1 p-2 border rounded dark:bg-gray-700 dark:text-gray-100"
                />
              </div>
              {/* End Date */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                  End Date
                </label>
                <input
                  type="date"
                  name="endDate"
                  value={filters.endDate}
                  onChange={handleFilterChange}
                  className="w-full mt-1 p-2 border rounded dark:bg-gray-700 dark:text-gray-100"
                />
              </div>
            </div>
          )}
        </aside>

        {/* Main content */}
        <main className="flex-1 p-6">
          {/* Header */}
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-3xl font-bold text-blue-600 dark:text-blue-300">
              üìä Dashboard
            </h1>
            <div className="flex items-center gap-3">
              <input
                type="text"
                className="p-2 border border-gray-300 dark:border-gray-700 rounded shadow-sm dark:bg-gray-800 dark:text-gray-100"
                placeholder="üîç Search transactions..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
              <button
                onClick={() => setDarkMode((d) => !d)}
                className="rounded px-3 py-2 text-xl focus:outline-none bg-gray-200 dark:bg-gray-700 dark:text-yellow-300 text-gray-800 hover:bg-gray-300 dark:hover:bg-gray-600 transition"
                title="Toggle dark mode"
              >
                {darkMode ? "‚òÄÔ∏è" : "üåô"}
              </button>
              <button
                onClick={handleLogout}
                className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
              >
                Logout
              </button>
            </div>
          </div>

          {/* Summary Metrics */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <div className="bg-white dark:bg-gray-800 rounded shadow p-4 flex flex-col items-center">
              <span className="text-gray-500 dark:text-gray-400">
                Total Transactions
              </span>
              <span className="text-2xl font-bold text-blue-600 dark:text-blue-300">
                {summaryMetrics.totalTransactions}
              </span>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded shadow p-4 flex flex-col items-center">
              <span className="text-gray-500 dark:text-gray-400">
                Total Amount
              </span>
              <span className="text-2xl font-bold text-green-600 dark:text-green-300">
                ‚Çπ{summaryMetrics.totalAmount}
              </span>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded shadow p-4 flex flex-col items-center">
              <span className="text-gray-500 dark:text-gray-400">
                Successful
              </span>
              <span className="text-2xl font-bold text-emerald-600 dark:text-emerald-300">
                {summaryMetrics.successful}
              </span>
            </div>
            <div className="bg-white dark:bg-gray-800 rounded shadow p-4 flex flex-col items-center">
              <span className="text-gray-500 dark:text-gray-400">
                Failed
              </span>
              <span className="text-2xl font-bold text-red-600 dark:text-red-300">
                {summaryMetrics.failed}
              </span>
            </div>
          </div>

          {/* Charts */}
          <div className="flex flex-col md:flex-row gap-6 mb-8">
            <div className="flex-1 bg-white dark:bg-gray-800 rounded shadow p-4">
              <h2 className="text-lg font-semibold mb-4 dark:text-gray-200">
                Category Distribution
              </h2>
              <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                  <Pie
                    data={categoryData}
                    dataKey="value"
                    nameKey="name"
                    cx="50%"
                    cy="50%"
                    outerRadius={80}
                    fill="#8884d8"
                    label
                  >
                    {categoryData.map((entry: CategoryData, index: number) => (
                      <Cell
                        key={`cell-${index}`}
                        fill={COLORS[index % COLORS.length]}
                      />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </div>
            <div className="flex-1 bg-white dark:bg-gray-800 rounded shadow p-4">
              <h2 className="text-lg font-semibold mb-4 dark:text-gray-200">
                Monthly / Weekly Amount
              </h2>
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={barData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  {groupByWeek ? (
                    <>
                      <Bar dataKey="W1" fill="#8884d8" />
                      <Bar dataKey="W2" fill="#82ca9d" />
                      <Bar dataKey="W3" fill="#ffc658" />
                      <Bar dataKey="W4" fill="#ff7f50" />
                      <Bar dataKey="W5" fill="#00c49f" />
                    </>
                  ) : (
                    <Bar dataKey="total" fill="#8884d8" />
                  )}
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Group by Week Toggle */}
          <div className="flex justify-between items-center mb-4">
            <label className="flex items-center gap-2 text-sm dark:text-gray-300">
              <input
                type="checkbox"
                checked={groupByWeek}
                onChange={(e) => setGroupByWeek(e.target.checked)}
              />
              Group by Week
            </label>
          </div>

          {/* Transaction Table */}
          <div className="bg-white dark:bg-gray-800 rounded shadow overflow-x-auto">
            <table className="min-w-full text-sm">
              <thead>
                <tr>
                  <th className="px-4 py-2 text-left">Date</th>
                  <th className="px-4 py-2 text-left">Description</th>
                  <th className="px-4 py-2 text-left">Category</th>
                  <th className="px-4 py-2 text-left">User</th>
                  <th className="px-4 py-2 text-left">Amount</th>
                  <th className="px-4 py-2 text-left">Status</th>
                </tr>
              </thead>
              <tbody>
                {filteredTransactions.length === 0 ? (
                  <tr>
                    <td
                      colSpan={6}
                      className="text-center py-6 text-gray-500"
                    >
                      No transactions found.
                    </td>
                  </tr>
                ) : (
                  filteredTransactions.map((txn: Transaction) => (
                    <tr
                      key={txn.id}
                      className="border-t dark:border-gray-700"
                    >
                      <td className="px-4 py-2">{txn.date}</td>
                      <td className="px-4 py-2">{txn.description}</td>
                      <td className="px-4 py-2">{txn.category}</td>
                      <td className="px-4 py-2">{txn.user}</td>
                      <td className="px-4 py-2">‚Çπ{txn.amount}</td>
                      <td className="px-4 py-2">
                        <span
                          className={
                            txn.status === "success"
                              ? "text-green-600 dark:text-green-300"
                              : txn.status === "failed"
                              ? "text-red-600 dark:text-red-300"
                              : "text-yellow-600 dark:text-yellow-300"
                          }
                        >
                          {txn.status.charAt(0).toUpperCase() +
                            txn.status.slice(1)}
                        </span>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
