import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  PieChart, Pie, Cell, Tooltip, Legend,
  BarChart, Bar, XAxis, YAxis, CartesianGrid, ResponsiveContainer
} from 'recharts';

interface Transaction {
  _id: string;
  date: string;
  amount: number;
  category: string;
  description?: string;
  user_id: string;
  status: 'Paid' | 'Pending';
}

function Dashboard() {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [filtered, setFiltered] = useState<Transaction[]>([]);
  const [error, setError] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [userFilter, setUserFilter] = useState('');
  const [minAmount, setMinAmount] = useState('');
  const [maxAmount, setMaxAmount] = useState('');
  const [dateFrom, setDateFrom] = useState('');
  const [dateTo, setDateTo] = useState('');
  const [groupByWeek, setGroupByWeek] = useState(false);

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  const navigate = useNavigate();

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = Array.isArray(res.data) ? res.data : res.data.transactions || [];
        setTransactions(data);
        setFiltered(data);
      } catch {
        setError('Failed to load transactions');
      }
    };
    fetchTransactions();
  }, []);

  useEffect(() => {
    let result = [...transactions];

    if (statusFilter) {
      result = result.filter(t => t.status === statusFilter);
    }

    if (categoryFilter) {
      result = result.filter(t => t.category === categoryFilter);
    }

    if (userFilter) {
      result = result.filter(t => t.user_id === userFilter);
    }

    if (minAmount) {
      result = result.filter(t => t.amount >= parseFloat(minAmount));
    }

    if (maxAmount) {
      result = result.filter(t => t.amount <= parseFloat(maxAmount));
    }

    if (dateFrom) {
      result = result.filter(t => new Date(t.date) >= new Date(dateFrom));
    }

    if (dateTo) {
      result = result.filter(t => new Date(t.date) <= new Date(dateTo));
    }

    setFiltered(result);
    setCurrentPage(1); // Reset page on filter
  }, [statusFilter, categoryFilter, userFilter, minAmount, maxAmount, dateFrom, dateTo, transactions]);

  const resetFilters = () => {
    setStatusFilter('');
    setCategoryFilter('');
    setUserFilter('');
    setMinAmount('');
    setMaxAmount('');
    setDateFrom('');
    setDateTo('');
    setGroupByWeek(false);
    setFiltered(transactions);
    setCurrentPage(1);
  };

  const exportToCSV = () => {
    const header = 'User ID,Status,Date,Amount,Category,Description\n';
    const rows = filtered.map(t => {
      const date = new Date(t.date).toISOString().split('T')[0];
      return `${t.user_id},${t.status},${date},${t.amount},${t.category || 'N/A'},${t.description || '-'}`;
    }).join('\n');

    const blob = new Blob([header + rows], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'transactions.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  const paginated = filtered.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);
  const totalPages = Math.ceil(filtered.length / itemsPerPage);

  const categoryData = Object.entries(
    filtered.reduce((acc, t) => {
      const key = t.category || 'Uncategorized';
      acc[key] = (acc[key] || 0) + t.amount;
      return acc;
    }, {} as Record<string, number>)
  ).map(([name, value]) => ({ name, value }));

  const monthlyData = Array.from({ length: 12 }, (_, i) => {
    const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
    const total = filtered
      .filter(t => new Date(t.date).getMonth() === i)
      .reduce((sum, t) => sum + t.amount, 0);
    return { name: monthName, total };
  });

  const uniqueCategories = [...new Set(transactions.map(t => t.category))];
  const uniqueUsers = [...new Set(transactions.map(t => t.user_id))];

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-blue-600">ðŸ“Š Dashboard</h1>
        <button
          onClick={() => {
            localStorage.removeItem('token');
            navigate('/');
          }}
          className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Logout
        </button>
      </div>

      {error && <p className="text-red-500 mb-4">{error}</p>}

      {/* Filters */}
      <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-3 mb-4">
        <select className="p-2 border rounded" value={statusFilter} onChange={e => setStatusFilter(e.target.value)}>
          <option value="">All Status</option>
          <option value="Paid">Paid</option>
          <option value="Pending">Pending</option>
        </select>

        <select className="p-2 border rounded" value={categoryFilter} onChange={e => setCategoryFilter(e.target.value)}>
          <option value="">All Categories</option>
          {uniqueCategories.map(c => <option key={c} value={c}>{c}</option>)}
        </select>

        <select className="p-2 border rounded" value={userFilter} onChange={e => setUserFilter(e.target.value)}>
          <option value="">All Users</option>
          {uniqueUsers.map(u => <option key={u} value={u}>{u}</option>)}
        </select>

        <input type="number" placeholder="Min â‚¹" className="p-2 border rounded" value={minAmount} onChange={e => setMinAmount(e.target.value)} />
        <input type="number" placeholder="Max â‚¹" className="p-2 border rounded" value={maxAmount} onChange={e => setMaxAmount(e.target.value)} />
        <input type="date" className="p-2 border rounded" value={dateFrom} onChange={e => setDateFrom(e.target.value)} />
        <input type="date" className="p-2 border rounded" value={dateTo} onChange={e => setDateTo(e.target.value)} />
        <button onClick={resetFilters} className="col-span-2 bg-gray-300 rounded p-2 hover:bg-gray-400">Reset Filters</button>
        <button onClick={exportToCSV} className="col-span-2 bg-green-600 text-white rounded p-2 hover:bg-green-700">Export CSV</button>
      </div>

      {/* Table */}
      <div className="overflow-x-auto mb-4">
        <table className="w-full bg-white rounded shadow">
          <thead className="bg-blue-600 text-white">
            <tr>
              <th className="p-2 text-left">User</th>
              <th className="p-2 text-left">Date</th>
              <th className="p-2 text-left">Amount</th>
              <th className="p-2 text-left">Category</th>
              <th className="p-2 text-left">Description</th>
              <th className="p-2 text-left">Status</th>
            </tr>
          </thead>
          <tbody>
            {paginated.map(t => (
              <tr key={t._id} className="border-t hover:bg-gray-100">
                <td className="p-2">{t.user_id}</td>
                <td className="p-2">{new Date(t.date).toLocaleDateString()}</td>
                <td className="p-2">â‚¹{t.amount}</td>
                <td className="p-2">{t.category}</td>
                <td className="p-2">{t.description || '-'}</td>
                <td className={`p-2 font-semibold ${t.status === 'Paid' ? 'text-green-600' : 'text-yellow-600'}`}>{t.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Pagination Controls */}
      <div className="flex justify-center gap-2 mb-6">
        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i}
            onClick={() => setCurrentPage(i + 1)}
            className={`px-3 py-1 rounded border ${currentPage === i + 1 ? 'bg-blue-600 text-white' : 'bg-white text-blue-600'}`}
          >
            {i + 1}
          </button>
        ))}
      </div>

      {/* Charts */}
      <h2 className="text-xl font-semibold mt-8 mb-4 text-blue-600">ðŸ“ˆ Spending by Category</h2>
      <ResponsiveContainer width="100%" height={300}>
        <PieChart>
          <Pie data={categoryData} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={100} label>
            {categoryData.map((_, i) => (
              <Cell key={i} fill={COLORS[i % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </ResponsiveContainer>

      <h2 className="text-xl font-semibold mt-8 mb-4 text-green-600">ðŸ“Š Monthly Spending</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={monthlyData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="total" fill="#82ca9d" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default Dashboard;
