          (!recentStatus || t.status === recentStatus) &&
          (!recentMonth || new Date(t.date).getMonth() + 1 === Number(recentMonth)) &&
          (!recentMinAmount || t.amount >= parseFloat(recentMinAmount)) &&
          (!recentMaxAmount || t.amount <= parseFloat(recentMaxAmount)) &&
          (!recentStartDate || new Date(t.date) >= new Date(recentStartDate)) &&
          (!recentEndDate || new Date(t.date) <= new Date(recentEndDate))
        )
        .slice(0, 5)
        .map((t) => (
