export default Dashboard;

import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  PieChart, Pie, Cell, Tooltip, Legend, BarChart, Bar,
  XAxis, YAxis, CartesianGrid, ResponsiveContainer, AreaChart, Area
} from 'recharts';

interface Transaction {
  _id: string;
  date: string;
  amount: number;
  category: string;
  description?: string;
  user_id: string;
  status: 'Paid' | 'Pending';
}

function Dashboard() {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [filtered, setFiltered] = useState<Transaction[]>([]);
  const [month, setMonth] = useState('');
  const [status, setStatus] = useState('');
  const [category, setCategory] = useState('');
  const [user, setUser] = useState('');
  const [minAmount, setMinAmount] = useState('');
  const [maxAmount, setMaxAmount] = useState('');
  const [groupByWeek, setGroupByWeek] = useState(false);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 10;
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');
  const [darkMode, setDarkMode] = useState(() => {
    if (typeof window !== "undefined") {
      const stored = localStorage.getItem('dashboard-darkmode');
      if (stored) return stored === 'true';
      return window.matchMedia('(prefers-color-scheme: dark)').matches;
    }
    return false;
  });
  const [showFilters, setShowFilters] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [showTransactions, setShowTransactions] = useState(false);

  const navigate = useNavigate();
  const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff7f50', '#00c49f', '#ffb6b9'];

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('dashboard-darkmode', 'true');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('dashboard-darkmode', 'false');
    }
  }, [darkMode]);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = Array.isArray(res.data) ? res.data : res.data.transactions || [];
        setTransactions(data);
        setFiltered(data);
      } catch (err) {
        setError('Failed to load transactions');
      }
    };
    fetchTransactions();
  }, []);

  useEffect(() => {
    let result = [...transactions];

    if (month) result = result.filter((t) => new Date(t.date).getMonth() + 1 === Number(month));
    if (status) result = result.filter((t) => t.status === status);
    if (category) result = result.filter((t) => t.category === category);
    if (user) result = result.filter((t) => t.user_id === user);
    if (minAmount) result = result.filter((t) => t.amount >= parseFloat(minAmount));
    if (maxAmount) result = result.filter((t) => t.amount <= parseFloat(maxAmount));
    if (startDate) result = result.filter((t) => new Date(t.date) >= new Date(startDate));
    if (endDate) result = result.filter((t) => new Date(t.date) <= new Date(endDate));

    if (search.trim()) {
      const s = search.trim().toLowerCase();
      result = result.filter((t) =>
        t.user_id?.toLowerCase().includes(s) ||
        t.status?.toLowerCase().includes(s) ||
        t.category?.toLowerCase().includes(s) ||
        t.description?.toLowerCase().includes(s) ||
        t.amount.toString().includes(s) ||
        new Date(t.date).toLocaleDateString().toLowerCase().includes(s)
      );
    }

    setCurrentPage(1);
    setFiltered(result);
  }, [
    month, status, category, user, minAmount, maxAmount,
    startDate, endDate, transactions, search
  ]);

  const exportToCSV = () => {
    const header = 'User ID,Status,Date,Amount,Category,Description\n';
    const rows = filtered
      .map((t) => {
        const date = new Date(t.date).toISOString().split('T')[0];
        return `${t.user_id},${t.status},${date},${t.amount},${t.category || 'N/A'},${t.description || '-'}`;
      })
      .join('\n');
    const blob = new Blob([header + rows], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'transactions.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const uniqueCategories = Array.from(new Set(transactions.map((t) => t.category)));
  const uniqueUsers = Array.from(new Set(transactions.map((t) => t.user_id)));

  const getWeek = (dateStr: string) => {
    const date = new Date(dateStr);
    const firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
    const dayOfWeek = firstDay.getDay();
    const adjustedDate = date.getDate() + dayOfWeek;
    return Math.ceil(adjustedDate / 7);
  };

  const categoryData = Object.entries(
    filtered.reduce((acc, t) => {
      const key = t.category || 'Uncategorized';
      acc[key] = (acc[key] || 0) + t.amount;
      return acc;
    }, {} as Record<string, number>)
  ).map(([name, value]) => ({ name, value }));

  const barData = groupByWeek
    ? Array.from({ length: 12 }, (_, i) => {
        const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
        const weeks = [0, 0, 0, 0, 0];
        filtered
          .filter((t) => new Date(t.date).getMonth() === i)
          .forEach((t) => {
            const week = getWeek(t.date);
            weeks[week - 1] += t.amount;
          });
        return { name: monthName, ...weeks.reduce((acc, val, idx) => ({ ...acc, [`W${idx + 1}`]: val }), {}) };
      })
    : Array.from({ length: 12 }, (_, i) => {
        const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
        const total = filtered
          .filter((t) => new Date(t.date).getMonth() === i)
          .reduce((sum, t) => sum + t.amount, 0);
        return { name: monthName, total };
      });

  // Recent transactions data for the sidebar
  const recentTransactions = [...transactions]
    .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime())
    .slice(0, 5);

  const totalEntries = filtered.length;
  const totalPages = Math.ceil(totalEntries / rowsPerPage);
  const startIndex = (currentPage - 1) * rowsPerPage;
  const currentEntries = filtered.slice(startIndex, startIndex + rowsPerPage);

  // Summary metrics
  const totalAmount = filtered.reduce((sum, t) => sum + t.amount, 0);
  const totalPaid = filtered.filter(t => t.status === 'Paid').reduce((sum, t) => sum + t.amount, 0);
  const totalPending = filtered.filter(t => t.status === 'Pending').reduce((sum, t) => sum + t.amount, 0);

  return (
    <div className="flex min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
      {/* Sidebar - Redesigned */}
      <div className="w-64 bg-indigo-700 dark:bg-gray-800 shadow-md p-4 flex flex-col text-white">
        <div className="flex items-center mb-8">
          <div className="w-10 h-10 rounded-full bg-white flex items-center justify-center mr-3">
            <span className="text-indigo-700 font-bold text-xl">$</span>
          </div>
          <h2 className="text-xl font-bold">Finance Dashboard</h2>
        </div>
        
        {/* Main Navigation */}
        <nav className="space-y-1 mb-8">
          <button 
            onClick={() => { setActiveTab('dashboard'); setShowTransactions(false); }}
            className={`flex items-center w-full p-3 rounded-lg ${activeTab === 'dashboard' ? 'bg-indigo-800 dark:bg-gray-700' : 'hover:bg-indigo-600 dark:hover:bg-gray-700'}`}
          >
            <span className="mr-3">üìä</span>
            Dashboard
          </button>
          
          <button 
            onClick={() => { setActiveTab('transactions'); setShowTransactions(true); }}
            className={`flex items-center w-full p-3 rounded-lg ${activeTab === 'transactions' ? 'bg-indigo-800 dark:bg-gray-700' : 'hover:bg-indigo-600 dark:hover:bg-gray-700'}`}
          >
            <span className="mr-3">üí≥</span>
            Transactions
          </button>
          
          <button 
            onClick={() => setActiveTab('accounts')}
            className={`flex items-center w-full p-3 rounded-lg ${activeTab === 'accounts' ? 'bg-indigo-800 dark:bg-gray-700' : 'hover:bg-indigo-600 dark:hover:bg-gray-700'}`}
          >
            <span className="mr-3">üè¶</span>
            Accounts
          </button>
          
          <button 
            onClick={() => setActiveTab('reports')}
            className={`flex items-center w-full p-3 rounded-lg ${activeTab === 'reports' ? 'bg-indigo-800 dark:bg-gray-700' : 'hover:bg-indigo-600 dark:hover:bg-gray-700'}`}
          >
            <span className="mr-3">üìà</span>
            Reports
          </button>
          
          <button 
            onClick={() => setActiveTab('settings')}
            className={`flex items-center w-full p-3 rounded-lg ${activeTab === 'settings' ? 'bg-indigo-800 dark:bg-gray-700' : 'hover:bg-indigo-600 dark:hover:bg-gray-700'}`}
          >
            <span className="mr-3">‚öôÔ∏è</span>
            Settings
          </button>
        </nav>
        
        {/* Recent Transactions Section */}
        <div className="mt-auto">
          <h3 className="text-sm font-semibold uppercase tracking-wider mb-2 opacity-70">Recent Transactions</h3>
          <div className="space-y-3">
            {recentTransactions.map((t) => (
              <div key={t._id} className="flex justify-between items-center p-2 bg-indigo-600 dark:bg-gray-700 rounded-lg">
                <div>
                  <p className="text-sm font-medium">{t.category || 'Other'}</p>
                  <p className="text-xs opacity-80">{new Date(t.date).toLocaleDateString()}</p>
                </div>
                <div className={`text-sm font-bold ${t.status === 'Paid' ? 'text-green-300' : 'text-yellow-300'}`}>
                  ‚Çπ{t.amount}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Main content */}
      <div className="flex-1 p-6">
        {/* Header with search and user controls */}
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl font-bold text-gray-800 dark:text-white">
            {activeTab === 'dashboard' && 'Dashboard Overview'}
            {activeTab === 'transactions' && 'Transaction History'}
            {activeTab === 'accounts' && 'Accounts Management'}
            {activeTab === 'reports' && 'Financial Reports'}
            {activeTab === 'settings' && 'Settings'}
          </h1>
          
          <div className="flex items-center space-x-4">
            <div className="relative">
              <input
                type="text"
                className="pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-full shadow-sm dark:bg-gray-800 dark:text-white"
                placeholder="Search..."
                value={search}
                onChange={e => setSearch(e.target.value)}
              />
              <span className="absolute left-3 top-2.5">üîç</span>
            </div>
            
            <button
              onClick={() => setDarkMode(d => !d)}
              className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600"
              title="Toggle dark mode"
            >
              {darkMode ? '‚òÄÔ∏è' : 'üåô'}
            </button>
            
            <div className="flex items-center">
              <div className="w-8 h-8 rounded-full bg-indigo-600 flex items-center justify-center text-white mr-2">
                {user ? user.charAt(0).toUpperCase() : 'U'}
              </div>
              <button
                onClick={handleLogout}
                className="text-sm text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white"
              >
                Logout
              </button>
            </div>
          </div>
        </div>

        {/* Dashboard Content */}
        {activeTab === 'dashboard' && (
          <>
            {/* Summary metrics - Redesigned */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
              <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 flex items-center">
                <div className="p-3 rounded-full bg-blue-100 dark:bg-blue-900 mr-4">
                  <span className="text-blue-600 dark:text-blue-300">üí∞</span>
                </div>
                <div>
                  <p className="text-gray-500 dark:text-gray-400 text-sm">Total Balance</p>
                  <p className="text-2xl font-bold text-gray-800 dark:text-white">‚Çπ{totalAmount.toLocaleString()}</p>
                </div>
              </div>
              
              <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 flex items-center">
                <div className="p-3 rounded-full bg-green-100 dark:bg-green-900 mr-4">
                  <span className="text-green-600 dark:text-green-300">‚Üë</span>
                </div>
                <div>
                  <p className="text-gray-500 dark:text-gray-400 text-sm">Income</p>
                  <p className="text-2xl font-bold text-gray-800 dark:text-white">‚Çπ{totalPaid.toLocaleString()}</p>
                </div>
              </div>
              
              <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 flex items-center">
                <div className="p-3 rounded-full bg-red-100 dark:bg-red-900 mr-4">
                  <span className="text-red-600 dark:text-red-300">‚Üì</span>
                </div>
                <div>
                  <p className="text-gray-500 dark:text-gray-400 text-sm">Expenses</p>
                  <p className="text-2xl font-bold text-gray-800 dark:text-white">‚Çπ{totalPending.toLocaleString()}</p>
                </div>
              </div>
              
              <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 flex items-center">
                <div className="p-3 rounded-full bg-purple-100 dark:bg-purple-900 mr-4">
                  <span className="text-purple-600 dark:text-purple-300">üìä</span>
                </div>
                <div>
                  <p className="text-gray-500 dark:text-gray-400 text-sm">Total Transactions</p>
                  <p className="text-2xl font-bold text-gray-800 dark:text-white">{filtered.length}</p>
                </div>
              </div>
            </div>

            {/* Charts Section */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
              {/* Spending Overview Chart */}
              <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-lg font-semibold text-gray-800 dark:text-white">Spending Overview</h2>
                  <select 
                    className="text-sm border-none bg-gray-100 dark:bg-gray-700 rounded-lg px-3 py-1"
                    value={month}
                    onChange={(e) => setMonth(e.target.value)}
                  >
                    <option value="">All Months</option>
                    {Array.from({ length: 12 }, (_, i) => (
                      <option key={i + 1} value={i + 1}>
                        {new Date(0, i).toLocaleString('default', { month: 'long' })}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="h-64">
                  <ResponsiveContainer width="100%" height="100%">
                    <AreaChart data={barData}>
                      <CartesianGrid strokeDasharray="3 3" stroke="#eee" strokeOpacity={darkMode ? 0.1 : 1} />
                      <XAxis dataKey="name" stroke={darkMode ? '#aaa' : '#666'} />
                      <YAxis stroke={darkMode ? '#aaa' : '#666'} />
                      <Tooltip />
                      <Area type="monotone" dataKey="total" stroke="#8884d8" fill="#8884d8" fillOpacity={0.2} />
                    </AreaChart>
                  </ResponsiveContainer>
                </div>
              </div>
              
              {/* Categories Chart */}
              { <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6">
                <h2 className="text-lg font-semibold text-gray-800 dark:text-white mb-4">Spending by Category</h2>
                <div className="h-64">
                  <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                      <Pie 
                        data={categoryData} 
                        dataKey="value" 
                        nameKey="name" 
                        cx="50%" 
                        cy="50%" 
                        outerRadius={80} 
                        fill="#8884d8" 
                        label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                      >
                        {categoryData.map((_, i) => (
                          <Cell key={i} fill={COLORS[i % COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip formatter={(value) => [`‚Çπ${value}`, 'Amount']} />
                      <Legend />
                    </PieChart>
                  </ResponsiveContainer>
                </div> 
               </div> }
            </div>}
            }

            {/* Recent Transactions Table */}
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow overflow-hidden">
              <div className="p-6 flex justify-between items-center">
                <h2 className="text-lg font-semibold text-gray-800 dark:text-white">Recent Transactions</h2>
                <button 
                  onClick={() => setShowTransactions(true)}
                  className="text-sm text-indigo-600 dark:text-indigo-400 hover:underline"
                >
                  View All
                </button>
              </div>
              
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-gray-50 dark:bg-gray-700">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Category</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Description</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Date</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Amount</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Status</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-gray-200 dark:divide-gray-700">
                    {transactions.slice(0, 5).map((t) => (
                      <tr key={t._id} className="hover:bg-gray-50 dark:hover:bg-gray-700">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="flex items-center">
                            <div className="flex-shrink-0 h-10 w-10 rounded-full bg-indigo-100 dark:bg-indigo-900 flex items-center justify-center">
                              <span className="text-indigo-600 dark:text-indigo-300">üí≥</span>
                            </div>
                            <div className="ml-4">
                              <div className="text-sm font-medium text-gray-900 dark:text-white">{t.category || 'Other'}</div>
                              <div className="text-sm text-gray-500 dark:text-gray-400">{t.user_id}</div>
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm text-gray-900 dark:text-white">{t.description || '-'}</div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm text-gray-900 dark:text-white">
                            {new Date(t.date).toLocaleDateString()}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900 dark:text-white">
                            ‚Çπ{t.amount}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                            ${t.status === 'Paid' ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200' : 
                              'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200'}`}>
                            {t.status}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </>
        )}

        {/* Transactions Tab Content */}
        {(activeTab === 'transactions' || showTransactions) && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow overflow-hidden">
            <div className="p-6 flex justify-between items-center">
              <h2 className="text-lg font-semibold text-gray-800 dark:text-white">Transaction History</h2>
              <div className="flex space-x-3">
                <button 
                  onClick={exportToCSV}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 text-sm"
                >
                  Export CSV
                </button>
                <button 
                  onClick={() => setShowFilters(!showFilters)}
                  className="px-4 py-2 bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-white rounded-lg hover:bg-gray-300 dark:hover:bg-gray-600 text-sm"
                >
                  {showFilters ? 'Hide Filters' : 'Show Filters'}
                </button>
              </div>
            </div>

            {/* Filters Section */}
            {showFilters && (
              <div className="px-6 pb-4 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <select 
                  className="p-2 border border-gray-300 dark:border-gray-600 rounded-lg dark:bg-gray-700 dark:text-white"
                  value={status}
                  onChange={(e) => setStatus(e.target.value)}
                >
                  <option value="">All Status</option>
                  <option value="Paid">Paid</option>
                  <option value="Pending">Pending</option>
                </select>
                
                <select 
                  className="p-2 border border-gray-300 dark:border-gray-600 rounded-lg dark:bg-gray-700 dark:text-white"
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                >
                  <option value="">All Categories</option>
                  {uniqueCategories.map((cat) => (
                    <option key={cat}>{cat}</option>
                  ))}
                </select>
                
                <input 
                  type="number" 
                  className="p-2 border border-gray-300 dark:border-gray-600 rounded-lg dark:bg-gray-700 dark:text-white"
                  placeholder="Min Amount"
                  value={minAmount}
                  onChange={(e) => setMinAmount(e.target.value)}
                />
                
                <input 
                  type="number" 
                  className="p-2 border border-gray-300 dark:border-gray-600 rounded-lg dark:bg-gray-700 dark:text-white"
                  placeholder="Max Amount"
                  value={maxAmount}
                  onChange={(e) => setMaxAmount(e.target.value)}
                />
              </div>
            )}

            {/* Transactions Table */}
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50 dark:bg-gray-700">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">User</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Date</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Amount</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Category</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Description</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Status</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200 dark:divide-gray-700">
                  {currentEntries.length === 0 ? (
                    <tr>
                      <td colSpan={6} className="px-6 py-4 text-center text-gray-500 dark:text-gray-400">
                        No transactions found.
                      </td>
                    </tr>
                  ) : (
                    currentEntries.map((t) => (
                      <tr key={t._id} className="hover:bg-gray-50 dark:hover:bg-gray-700">
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                          {t.user_id}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                          {new Date(t.date).toLocaleDateString()}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white">
                          ‚Çπ{t.amount}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                          {t.category || 'N/A'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                          {t.description || '-'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                            ${t.status === 'Paid' ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200' : 
                              'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200'}`}>
                            {t.status}
                          </span>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>

            {/* Pagination */}
            <div className="px-6 py-4 flex items-center justify-between border-t border-gray-200 dark:border-gray-700">
              <div className="flex-1 flex justify-between sm:hidden">
                <button 
                  onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
                  disabled={currentPage === 1}
                  className="relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
                >
                  Previous
                </button>
                <button 
                  onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
                  disabled={currentPage === totalPages}
                  className="ml-3 relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
                >
                  Next
                </button>
              </div>
              <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
                <div>
                  <p className="text-sm text-gray-700 dark:text-gray-300">
                    Showing <span className="font-medium">{startIndex + 1}</span> to <span className="font-medium">{Math.min(startIndex + rowsPerPage, totalEntries)}</span> of <span className="font-medium">{totalEntries}</span> results
                  </p>
                </div>
                <div>
                  <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
                    <button
                      onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
                      disabled={currentPage === 1}
                      className="relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 dark:bg-gray-700 dark:border-gray-600 dark:text-gray-300"
                    >
                      <span className="sr-only">Previous</span>
                      &larr;
                    </button>
                    {Array.from({ length: totalPages }, (_, i) => (
                      <button
                        key={i + 1}
                        onClick={() => setCurrentPage(i + 1)}
                        className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                          currentPage === i + 1
                            ? 'z-10 bg-indigo-50 border-indigo-500 text-indigo-600 dark:bg-indigo-900 dark:border-indigo-700 dark:text-indigo-200'
                            : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50 dark:bg-gray-700 dark:border-gray-600 dark:text-gray-300'
                        }`}
                      >
                        {i + 1}
                      </button>
                    ))}
                    <button
                      onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
                      disabled={currentPage === totalPages}
                      className="relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 dark:bg-gray-700 dark:border-gray-600 dark:text-gray-300"
                    >
                      <span className="sr-only">Next</span>
                      &rarr;
                    </button>
                  </nav>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Other Tab Content Placeholders */}
        {activeTab === 'accounts' && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6">
            <h2 className="text-lg font-semibold text-gray-800 dark:text-white mb-4">Accounts Management</h2>
            <p className="text-gray-600 dark:text-gray-300">Your accounts management content will appear here.</p>
          </div>
        )}

        {activeTab === 'reports' && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6">
            <h2 className="text-lg font-semibold text-gray-800 dark:text-white mb-4">Financial Reports</h2>
            <p className="text-gray-600 dark:text-gray-300">Your financial reports will appear here.</p>
          </div>
        )}

        {activeTab === 'settings' && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-6">
            <h2 className="text-lg font-semibold text-gray-800 dark:text-white mb-4">Settings</h2>
            <p className="text-gray-600 dark:text-gray-300">Your settings will appear here.</p>
          </div>
        )}
      </div>
    </div>
  );
}

