import { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  PieChart, Pie, Cell, Tooltip, Legend, BarChart, Bar,
  XAxis, YAxis, CartesianGrid, ResponsiveContainer, Label, Line
} from 'recharts';
import * as htmlToImage from 'html-to-image';

interface Transaction {
  _id: string;
  date: string;
  amount: number;
  category: string;
  description?: string;
  user_id: string;
  status: 'Paid' | 'Pending';
}

const COLOR_PALETTE = [
  '#377eb8', '#4daf4a', '#984ea3', '#ff7f00', '#e41a1c', '#a65628', '#f781bf', '#999999'
];

function Dashboard() {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [filtered, setFiltered] = useState<Transaction[]>([]);
  const [month, setMonth] = useState('');
  const [status, setStatus] = useState('');
  const [category, setCategory] = useState('');
  const [user, setUser] = useState('');
  const [minAmount, setMinAmount] = useState('');
  const [maxAmount, setMaxAmount] = useState('');
  const [groupByWeek, setGroupByWeek] = useState(false);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 10;
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');
  const [darkMode, setDarkMode] = useState(() => {
    if (typeof window !== "undefined") {
      const stored = localStorage.getItem('dashboard-darkmode');
      if (stored) return stored === 'true';
      return window.matchMedia('(prefers-color-scheme: dark)').matches;
    }
    return false;
  });

  // Chart enhancements
  const [barChartType, setBarChartType] = useState<'bar' | 'line'>('bar');
  const chartRef = useRef<HTMLDivElement>(null);

  const navigate = useNavigate();

  // Dark mode
  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('dashboard-darkmode', 'true');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('dashboard-darkmode', 'false');
    }
  }, [darkMode]);

  // Fetch transactions
  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = Array.isArray(res.data) ? res.data : res.data.transactions || [];
        setTransactions(data);
        setFiltered(data);
      } catch (err) {
        setError('Failed to load transactions');
      }
    };
    fetchTransactions();
  }, []);

  // Filtering and searching
  useEffect(() => {
    let result = [...transactions];

    if (month) result = result.filter((t) => new Date(t.date).getMonth() + 1 === Number(month));
    if (status) result = result.filter((t) => t.status === status);
    if (category) result = result.filter((t) => t.category === category);
    if (user) result = result.filter((t) => t.user_id === user);
    if (minAmount) result = result.filter((t) => t.amount >= parseFloat(minAmount));
    if (maxAmount) result = result.filter((t) => t.amount <= parseFloat(maxAmount));
    if (startDate) result = result.filter((t) => new Date(t.date) >= new Date(startDate));
    if (endDate) result = result.filter((t) => new Date(t.date) <= new Date(endDate));
    if (search.trim()) {
      const s = search.trim().toLowerCase();
      result = result.filter((t) =>
        t.user_id?.toLowerCase().includes(s) ||
        t.status?.toLowerCase().includes(s) ||
        t.category?.toLowerCase().includes(s) ||
        t.description?.toLowerCase().includes(s) ||
        t.amount.toString().includes(s) ||
        new Date(t.date).toLocaleDateString().toLowerCase().includes(s)
      );
    }
    setCurrentPage(1);
    setFiltered(result);
  }, [
    month, status, category, user, minAmount, maxAmount,
    startDate, endDate, transactions, search
  ]);

  const exportToCSV = () => {
    const header = 'User ID,Status,Date,Amount,Category,Description\n';
    const rows = filtered
      .map((t) => {
        const date = new Date(t.date).toISOString().split('T')[0];
        return `${t.user_id},${t.status},${date},${t.amount},${t.category || 'N/A'},${t.description || '-'}`;
      })
      .join('\n');
    const blob = new Blob([header + rows], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'transactions.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  const exportChartAsPNG = () => {
    if (chartRef.current) {
      htmlToImage.toPng(chartRef.current)
        .then((dataUrl) => {
          const link = document.createElement('a');
          link.download = 'chart.png';
          link.href = dataUrl;
          link.click();
        });
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const uniqueCategories = Array.from(new Set(transactions.map((t) => t.category)));
  const uniqueUsers = Array.from(new Set(transactions.map((t) => t.user_id)));

  const getWeek = (dateStr: string) => {
    const date = new Date(dateStr);
    const firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
    const dayOfWeek = firstDay.getDay();
    const adjustedDate = date.getDate() + dayOfWeek;
    return Math.ceil(adjustedDate / 7);
  };

  // Pie chart data
  const categoryData = Object.entries(
    filtered.reduce((acc, t) => {
      const key = t.category || 'Uncategorized';
      acc[key] = (acc[key] || 0) + t.amount;
      return acc;
    }, {} as Record<string, number>)
  ).map(([name, value]) => ({ name, value }));

  // Bar/Line chart data
  const barData = groupByWeek
    ? Array.from({ length: 12 }, (_, i) => {
        const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
        const weeks = [0, 0, 0, 0, 0];
        filtered
          .filter((t) => new Date(t.date).getMonth() === i)
          .forEach((t) => {
            const week = getWeek(t.date);
            weeks[week - 1] += t.amount;
          });
        return { name: monthName, ...weeks.reduce((acc, val, idx) => ({ ...acc, [`W${idx + 1}`]: val }), {}) };
      })
    : Array.from({ length: 12 }, (_, i) => {
        const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
        const total = filtered
          .filter((t) => new Date(t.date).getMonth() === i)
          .reduce((sum, t) => sum + t.amount, 0);
        return { name: monthName, total };
      });

  const totalEntries = filtered.length;
  const totalPages = Math.ceil(totalEntries / rowsPerPage);
  const startIndex = (currentPage - 1) * rowsPerPage;
  const currentEntries = filtered.slice(startIndex, startIndex + rowsPerPage);

  // Summary metrics
  const totalAmount = filtered.reduce((sum, t) => sum + t.amount, 0);
  const totalPaid = filtered.filter(t => t.status === 'Paid').reduce((sum, t) => sum + t.amount, 0);
  const totalPending = filtered.filter(t => t.status === 'Pending').reduce((sum, t) => sum + t.amount, 0);

  // Pie chart drill-down
  const handlePieClick = (data: any) => {
    if (data && data.name) setCategory(data.name);
  };

  return (
    <div className="p-6 min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-blue-600 dark:text-blue-300">📊 Dashboard</h1>
        <div className="flex gap-3 items-center">
          <button
            onClick={() => setDarkMode(d => !d)}
            className="rounded px-3 py-2 text-xl focus:outline-none bg-gray-200 dark:bg-gray-700 dark:text-yellow-300 text-gray-800 hover:bg-gray-300 dark:hover:bg-gray-600 transition"
            title="Toggle dark mode"
          >
            {darkMode ? '☀️' : '🌙'}
          </button>
          <button
            onClick={handleLogout}
            className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
          >
            Logout
          </button>
        </div>
      </div>

      {/* Search */}
      <div className="mb-4 flex">
        <input
          type="text"
          className="w-full md:w-1/3 p-2 border border-gray-300 dark:border-gray-700 rounded shadow-sm dark:bg-gray-800 dark:text-gray-100"
          placeholder="🔍 Search transactions (user, status, amount, date, category, description)..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
      </div>

      {/* Summary metrics */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <div className="bg-white dark:bg-gray-800 rounded shadow p-4 flex flex-col items-center">
          <span className="text-gray-500 dark:text-gray-400 text-sm">Total Transactions</span>
          <span className="text-2xl font-bold text-blue-700 dark:text-blue-300">{filtered.length}</span>
        </div>
        <div className="bg-white dark:bg-gray-800 rounded shadow p-4 flex flex-col items-center">
          <span className="text-gray-500 dark:text-gray-400 text-sm">Total Amount</span>
          <span className="text-2xl font-bold text-green-700 dark:text-green-400">₹{totalAmount.toLocaleString()}</span>
        </div>
        <div className="bg-white dark:bg-gray-800 rounded shadow p-4 flex flex-col items-center">
          <span className="text-gray-500 dark:text-gray-400 text-sm">Paid Amount</span>
          <span className="text-2xl font-bold text-green-600 dark:text-green-300">₹{totalPaid.toLocaleString()}</span>
        </div>
        <div className="bg-white dark:bg-gray-800 rounded shadow p-4 flex flex-col items-center">
          <span className="text-gray-500 dark:text-gray-400 text-sm">Pending Amount</span>
          <span className="text-2xl font-bold text-yellow-600 dark:text-yellow-400">₹{totalPending.toLocaleString()}</span>
        </div>
      </div>

      {error && <p className="text-red-500 mb-4">{error}</p>}

      {/* Filters */}
      <div className="grid md:grid-cols-3 lg:grid-cols-6 gap-4 mb-4">
        <select className="p-2 border rounded dark:bg-gray-800 dark:border-gray-700 dark:text-gray-100" value={month} onChange={(e) => setMonth(e.target.value)}>
          <option value="">All Months</option>
          {Array.from({ length: 12 }, (_, i) => (
            <option key={i + 1} value={i + 1}>
              {new Date(0, i).toLocaleString('default', { month: 'long' })}
            </option>
          ))}
        </select>
        <select className="p-2 border rounded dark:bg-gray-800 dark:border-gray-700 dark:text-gray-100" value={status} onChange={(e) => setStatus(e.target.value)}>
          <option value="">All Status</option>
          <option value="Paid">Paid</option>
          <option value="Pending">Pending</option>
        </select>
        <select className="p-2 border rounded dark:bg-gray-800 dark:border-gray-700 dark:text-gray-100" value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">All Categories</option>
          {uniqueCategories.map((cat) => (
            <option key={cat}>{cat}</option>
          ))}
        </select>
        <select className="p-2 border rounded dark:bg-gray-800 dark:border-gray-700 dark:text-gray-100" value={user} onChange={(e) => setUser(e.target.value)}>
          <option value="">All Users</option>
          {uniqueUsers.map((u) => (
            <option key={u}>{u}</option>
          ))}
        </select>
        <input type="number" className="p-2 border rounded dark:bg-gray-800 dark:border-gray-700 dark:text-gray-100" placeholder="Min ₹" value={minAmount} onChange={(e) => setMinAmount(e.target.value)} />
        <input type="number" className="p-2 border rounded dark:bg-gray-800 dark:border-gray-700 dark:text-gray-100" placeholder="Max ₹" value={maxAmount} onChange={(e) => setMaxAmount(e.target.value)} />
      </div>
      <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        <input type="date" className="p-2 border rounded dark:bg-gray-800 dark:border-gray-700 dark:text-gray-100" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
        <input type="date" className="p-2 border rounded dark:bg-gray-800 dark:border-gray-700 dark:text-gray-100" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
        <button onClick={exportToCSV} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">Export CSV</button>
        <button onClick={() => {
          setMonth(''); setStatus(''); setCategory(''); setUser('');
          setMinAmount(''); setMaxAmount(''); setStartDate('');
          setEndDate(''); setGroupByWeek(false); setCurrentPage(1);
          setFiltered(transactions);
          setSearch('');
        }} className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600">Reset Filters</button>
      </div>

      <label className="flex items-center gap-2 mb-4 text-sm dark:text-gray-300">
        <input type="checkbox" checked={groupByWeek} onChange={(e) => setGroupByWeek(e.target.checked)} />
        Group by Week
      </label>

      {/* --- CHARTS SECTION --- */}
      <div className="grid md:grid-cols-2 gap-8 mb-12">
        {/* --- PIE CHART: CATEGORY DISTRIBUTION --- */}
        <div className="bg-white dark:bg-gray-800 rounded shadow p-4">
          <h2 className="text-lg font-semibold mb-2 text-center text-blue-700 dark:text-blue-300">Transactions by Category</h2>
          <ResponsiveContainer width="100%" height={350}>
            <PieChart>
              <Pie
                data={categoryData}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, value, percent }) => `${name}: ₹${value.toLocaleString()} (${(percent * 100).toFixed(1)}%)`}
                outerRadius={110}
                dataKey="value"
                onClick={handlePieClick}
                isAnimationActive
              >
                {categoryData.map((entry, idx) => (
                  <Cell key={`cell-${idx}`} fill={COLOR_PALETTE[idx % COLOR_PALETTE.length]} />
                ))}
              </Pie>
              <Tooltip formatter={(v) => `₹${v.toLocaleString()}`} />
              <Legend
                verticalAlign="bottom"
                iconType="circle"
                onClick={(e) => setCategory(e.value)}
                payload={categoryData.map((item, idx) => ({
                  value: item.name,
                  type: "circle",
                  color: COLOR_PALETTE[idx % COLOR_PALETTE.length],
                  id: item.name
                }))}
              />
            </PieChart>
          </ResponsiveContainer>
          <div className="text-xs text-gray-500 dark:text-gray-400 text-center mt-2">
            Click a slice or legend to filter by category
          </div>
        </div>

        {/* --- BAR/LINE CHART: MONTHLY/WEEKLY AMOUNT --- */}
        <div className="bg-white dark:bg-gray-800 rounded shadow p-4">
          <div className="flex justify-between items-center mb-2">
            <h2 className="text-lg font-semibold text-blue-700 dark:text-blue-300">
              {groupByWeek ? 'Weekly' : 'Monthly'} Transaction Amounts
            </h2>
            <div className="flex gap-2">
              <button
                className={`px-2 py-1 rounded ${barChartType === 'bar' ? 'bg-blue-600 text-white' : 'bg-gray-200 dark:bg-gray-700 dark:text-gray-300'}`}
                onClick={() => setBarChartType('bar')}
              >Bar</button>
              <button
                className={`px-2 py-1 rounded ${barChartType === 'line' ? 'bg-blue-600 text-white' : 'bg-gray-200 dark:bg-gray-700 dark:text-gray-300'}`}
                onClick={() => setBarChartType('line')}
              >Line</button>
              <button
                className="px-2 py-1 bg-green-600 text-white rounded"
                onClick={exportChartAsPNG}
                title="Download chart as PNG"
              >Export PNG</button>
            </div>
          </div>
          <div ref={chartRef}>
            <ResponsiveContainer width="100%" height={350}>
              <BarChart data={barData} margin={{ top: 10, right: 30, left: 0, bottom: 25 }}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name">
                  <Label value={groupByWeek ? "Month" : "Month"} offset={0} position="insideBottom" />
                </XAxis>
                <YAxis>
                  <Label value="Amount (₹)" angle={-90} position="insideLeft" style={{ textAnchor: 'middle' }} />
                </YAxis>
                <Tooltip formatter={(v) => `₹${v.toLocaleString()}`} />
                <Legend />
                {groupByWeek
                  ? [1, 2, 3, 4, 5].map(w => (
                      <Bar
                        key={`W${w}`}
                        dataKey={`W${w}`}
                        stackId="a"
                        fill={COLOR_PALETTE[w % COLOR_PALETTE.length]}
                        label={{ position: 'top', formatter: (v: any) => v > 0 ? `₹${v}` : '' }}
                      />
                    ))
                  : (
                    <>
                      {barChartType === 'bar' && (
                        <Bar
                          dataKey="total"
                          fill={COLOR_PALETTE[0]}
                          label={{ position: 'top', formatter: (v: any) => v > 0 ? `₹${v}` : '' }}
                        />
                      )}
                      {barChartType === 'line' && (
                        <Line
                          type="monotone"
                          dataKey="total"
                          stroke={COLOR_PALETTE[1]}
                          strokeWidth={3}
                          dot={{ r: 5, fill: COLOR_PALETTE[1] }}
                          activeDot={{ r: 7 }}
                        />
                      )}
                    </>
                  )
                }
              </BarChart>
            </ResponsiveContainer>
          </div>
          <div className="text-xs text-gray-500 dark:text-gray-400 text-center mt-2">
            Toggle chart type or download as image for reports
          </div>
        </div>
      </div>

      {/* Table */}
      <div className="overflow-x-auto mb-2">
        <table className="w-full bg-white dark:bg-gray-800 rounded shadow-md">
          <thead className="bg-blue-600 dark:bg-blue-900 text-white">
            <tr>
              <th className="p-3 text-left">User ID</th>
              <th className="p-3 text-left">Date</th>
              <th className="p-3 text-left">Amount (₹)</th>
              <th className="p-3 text-left">Category</th>
              <th className="p-3 text-left">Description</th>
              <th className="p-3 text-left">Status</th>
            </tr>
          </thead>
          <tbody>
            {currentEntries.length === 0 ? (
              <tr>
                <td colSpan={6} className="p-4 text-center text-gray-500 dark:text-gray-400">No transactions found.</td>
              </tr>
            ) : (
              currentEntries.map((t) => (
                <tr key={t._id} className="border-t hover:bg-gray-50 dark:hover:bg-gray-700">
                  <td className="p-3">{t.user_id}</td>
                  <td className="p-3">{new Date(t.date).toLocaleDateString()}</td>
                  <td className="p-3">₹{t.amount}</td>
                  <td className="p-3">{t.category || 'N/A'}</td>
                  <td className="p-3">{t.description || '-'}</td>
                  <td className={`p-3 font-medium ${t.status === 'Paid' ? 'text-green-600 dark:text-green-400' : 'text-yellow-600 dark:text-yellow-400'}`}>{t.status}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      <div className="text-sm text-gray-600 dark:text-gray-300 mb-2 text-center">
        Showing {startIndex + 1}–{Math.min(startIndex + rowsPerPage, totalEntries)} of {totalEntries} entries
      </div>
      <div className="flex items-center justify-center gap-4 mb-10">
        <button onClick={() => setCurrentPage((p) => Math.max(1, p - 1))} className="px-3 py-1 bg-gray-300 dark:bg-gray-700 rounded">
          Prev
        </button>
        <span className="dark:text-gray-200">Page</span>
        <input
          type="number"
          min={1}
          max={totalPages}
          value={currentPage}
          onChange={(e) => {
            const val = parseInt(e.target.value);
            if (!isNaN(val) && val >= 1 && val <= totalPages) {
              setCurrentPage(val);
            }
          }}
          className="w-16 p-1 border rounded text-center dark:bg-gray-800 dark:border-gray-700 dark:text-gray-100"
        />
        <span className="dark:text-gray-200">of {totalPages}</span>
        <button onClick={() => setCurrentPage((p) => Math.min(totalPages, p + 1))} className="px-3 py-1 bg-gray-300 dark:bg-gray-700 rounded">
          Next
        </button>
      </div>
    </div>
  );
}

export default Dashboard;
