// FULL UPDATED CODE FOR Dashboard.tsx with Sidebar Layout and Table/CSV Fixes

import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  PieChart, Pie, Cell, Tooltip, Legend, BarChart, Bar,
  XAxis, YAxis, CartesianGrid, ResponsiveContainer
} from 'recharts';

interface Transaction {
  _id: string;
  date: string;
  amount: number;
  category: string;
  description?: string;
  user_id: string;
  status: 'Paid' | 'Pending';
}

function Dashboard() {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [filtered, setFiltered] = useState<Transaction[]>([]);
  const [month, setMonth] = useState('');
  const [status, setStatus] = useState('');
  const [category, setCategory] = useState('');
  const [user, setUser] = useState('');
  const [minAmount, setMinAmount] = useState('');
  const [maxAmount, setMaxAmount] = useState('');
  const [groupByWeek, setGroupByWeek] = useState(false);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [search, setSearch] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [error, setError] = useState('');

  const navigate = useNavigate();
  const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff7f50', '#00c49f', '#ffb6b9'];
  const rowsPerPage = 10;

  const [darkMode, setDarkMode] = useState(() => {
    if (typeof window !== "undefined") {
      const stored = localStorage.getItem('dashboard-darkmode');
      if (stored) return stored === 'true';
      return window.matchMedia('(prefers-color-scheme: dark)').matches;
    }
    return false;
  });

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('dashboard-darkmode', 'true');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('dashboard-darkmode', 'false');
    }
  }, [darkMode]);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/transactions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = Array.isArray(res.data) ? res.data : res.data.transactions || [];
        setTransactions(data);
        setFiltered(data);
      } catch (err) {
        setError('Failed to load transactions');
      }
    };
    fetchTransactions();
  }, []);

  useEffect(() => {
    let result = [...transactions];

    if (month) result = result.filter((t) => new Date(t.date).getMonth() + 1 === Number(month));
    if (status) result = result.filter((t) => t.status === status);
    if (category) result = result.filter((t) => t.category === category);
    if (user) result = result.filter((t) => t.user_id === user);
    if (minAmount) result = result.filter((t) => t.amount >= parseFloat(minAmount));
    if (maxAmount) result = result.filter((t) => t.amount <= parseFloat(maxAmount));
    if (startDate) result = result.filter((t) => new Date(t.date) >= new Date(startDate));
    if (endDate) result = result.filter((t) => new Date(t.date) <= new Date(endDate));
    if (search.trim()) {
      const s = search.trim().toLowerCase();
      result = result.filter((t) =>
        t.user_id?.toLowerCase().includes(s) ||
        t.status?.toLowerCase().includes(s) ||
        t.category?.toLowerCase().includes(s) ||
        t.description?.toLowerCase().includes(s) ||
        t.amount.toString().includes(s) ||
        new Date(t.date).toLocaleDateString().toLowerCase().includes(s)
      );
    }

    setCurrentPage(1);
    setFiltered(result);
  }, [month, status, category, user, minAmount, maxAmount, startDate, endDate, transactions, search]);

  const exportToCSV = () => {
    const header = 'User ID,Status,Date,Amount,Category,Description\n';
    const rows = filtered
      .map((t) => {
        const date = new Date(t.date).toISOString().split('T')[0];
        return `${t.user_id},${t.status},${date},${t.amount},${t.category || 'N/A'},${t.description || '-'}`;
      })
      .join('\n');
    const blob = new Blob([header + rows], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'transactions.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  const resetFilters = () => {
    setMonth(''); setStatus(''); setCategory(''); setUser('');
    setMinAmount(''); setMaxAmount(''); setStartDate('');
    setEndDate(''); setGroupByWeek(false); setCurrentPage(1);
    setFiltered(transactions); setSearch('');
  };

  const uniqueCategories = Array.from(new Set(transactions.map((t) => t.category)));
  const uniqueUsers = Array.from(new Set(transactions.map((t) => t.user_id)));

  const getWeek = (dateStr: string) => {
    const date = new Date(dateStr);
    const firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
    const dayOfWeek = firstDay.getDay();
    const adjustedDate = date.getDate() + dayOfWeek;
    return Math.ceil(adjustedDate / 7);
  };

  const categoryData = Object.entries(
    filtered.reduce((acc, t) => {
      const key = t.category || 'Uncategorized';
      acc[key] = (acc[key] || 0) + t.amount;
      return acc;
    }, {} as Record<string, number>)
  ).map(([name, value]) => ({ name, value }));

  const barData = groupByWeek
    ? Array.from({ length: 12 }, (_, i) => {
        const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
        const weeks = [0, 0, 0, 0, 0];
        filtered.filter((t) => new Date(t.date).getMonth() === i).forEach((t) => {
          const week = getWeek(t.date);
          weeks[week - 1] += t.amount;
        });
        return { name: monthName, ...weeks.reduce((acc, val, idx) => ({ ...acc, [`W${idx + 1}`]: val }), {}) };
      })
    : Array.from({ length: 12 }, (_, i) => {
        const monthName = new Date(0, i).toLocaleString('default', { month: 'short' });
        const total = filtered.filter((t) => new Date(t.date).getMonth() === i).reduce((sum, t) => sum + t.amount, 0);
        return { name: monthName, total };
      });

  const totalEntries = filtered.length;
  const totalPages = Math.ceil(totalEntries / rowsPerPage);
  const startIndex = (currentPage - 1) * rowsPerPage;
  const currentEntries = filtered.slice(startIndex, startIndex + rowsPerPage);

  const totalAmount = filtered.reduce((sum, t) => sum + t.amount, 0);
  const totalPaid = filtered.filter(t => t.status === 'Paid').reduce((sum, t) => sum + t.amount, 0);
  const totalPending = filtered.filter(t => t.status === 'Pending').reduce((sum, t) => sum + t.amount, 0);

  return (
    <div className="flex">
      <div className="w-64 bg-white dark:bg-gray-800 min-h-screen p-4">
        <h2 className="text-xl font-semibold mb-4 text-blue-600 dark:text-blue-300">Sidebar</h2>
        <button
          onClick={() => setShowFilters(!showFilters)}
          className="mb-4 px-3 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          {showFilters ? 'Hide Filters' : 'Show Filters'}
        </button>

        {showFilters && (
          <div className="space-y-3">
            <select className="w-full p-2 rounded dark:bg-gray-700" value={month} onChange={(e) => setMonth(e.target.value)}>
              <option value="">All Months</option>
              {Array.from({ length: 12 }, (_, i) => (
                <option key={i + 1} value={i + 1}>{new Date(0, i).toLocaleString('default', { month: 'long' })}</option>
              ))}
            </select>
            <select className="w-full p-2 rounded dark:bg-gray-700" value={status} onChange={(e) => setStatus(e.target.value)}>
              <option value="">All Status</option>
              <option value="Paid">Paid</option>
              <option value="Pending">Pending</option>
            </select>
            <select className="w-full p-2 rounded dark:bg-gray-700" value={category} onChange={(e) => setCategory(e.target.value)}>
              <option value="">All Categories</option>
              {uniqueCategories.map((cat) => <option key={cat}>{cat}</option>)}
            </select>
            <select className="w-full p-2 rounded dark:bg-gray-700" value={user} onChange={(e) => setUser(e.target.value)}>
              <option value="">All Users</option>
              {uniqueUsers.map((u) => <option key={u}>{u}</option>)}
            </select>
            <input type="number" className="w-full p-2 rounded dark:bg-gray-700" placeholder="Min ‚Çπ" value={minAmount} onChange={(e) => setMinAmount(e.target.value)} />
            <input type="number" className="w-full p-2 rounded dark:bg-gray-700" placeholder="Max ‚Çπ" value={maxAmount} onChange={(e) => setMaxAmount(e.target.value)} />
            <input type="date" className="w-full p-2 rounded dark:bg-gray-700" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
            <input type="date" className="w-full p-2 rounded dark:bg-gray-700" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
            <button onClick={exportToCSV} className="w-full bg-green-600 text-white px-4 py-2 rounded">Export CSV</button>
            <button onClick={resetFilters} className="w-full bg-gray-500 text-white px-4 py-2 rounded">Reset Filters</button>
          </div>
        )}
      </div>

      <div className="flex-1 p-6 bg-gray-100 dark:bg-gray-900">
        {/* Add summary, charts, and table layout here */}
        <label className="flex items-center gap-2 mb-4">
          <input type="checkbox" checked={groupByWeek} onChange={e => setGroupByWeek(e.target.checked)} />
          Group by Week
        </label>

        <div className="overflow-x-auto mb-6">
          <table className="w-full bg-white dark:bg-gray-800 rounded shadow-md">
            <thead className="bg-blue-600 dark:bg-blue-900 text-white">
              <tr>
                <th className="p-3 text-left">User ID</th>
                <th className="p-3 text-left">Date</th>
                <th className="p-3 text-left">Amount (‚Çπ)</th>
                <th className="p-3 text-left">Category</th>
                <th className="p-3 text-left">Description</th>
                <th className="p-3 text-left">Status</th>
              </tr>
            </thead>
            <tbody>
              {currentEntries.length === 0 ? (
                <tr>
                  <td colSpan={6} className="p-4 text-center text-gray-500 dark:text-gray-400">No transactions found.</td>
                </tr>
              ) : (
                currentEntries.map((t) => (
                  <tr key={t._id} className="border-t hover:bg-gray-50 dark:hover:bg-gray-700">
                    <td className="p-3">{t.user_id}</td>
                    <td className="p-3">{new Date(t.date).toLocaleDateString()}</td>
                    <td className="p-3">‚Çπ{t.amount}</td>
                    <td className="p-3">{t.category || 'N/A'}</td>
                    <td className="p-3">{t.description || '-'}</td>
                    <td className={`p-3 font-medium ${t.status === 'Paid' ? 'text-green-600 dark:text-green-400' : 'text-yellow-600 dark:text-yellow-400'}`}>{t.status}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
